int pgpPrtParams(const uint8_t * pkts, size_t pktlen, unsigned int pkttype,
		 pgpDigParams * ret)
{
    const uint8_t *p = pkts;
    const uint8_t *pend = pkts + pktlen;
    pgpDigParams digp = NULL;
    struct pgpPkt pkt;
    int rc = -1; 
    while (p < pend) {
	if (decodePkt(p, (pend - p), &pkt))
	    break;
	if (digp == NULL) {
	    if (pkttype && pkt.tag != pkttype) {
		break;
	    } else {
		digp = pgpDigParamsNew(pkt.tag);
	    }
	}
	if (pgpPrtPkt(&pkt, digp))
	    break;
	p += (pkt.body - pkt.head) + pkt.blen;
	if (pkttype == PGPTAG_SIGNATURE)
	    break;
    }
    rc = (digp && (p == pend)) ? 0 : -1;
    if (ret && rc == 0) {
	*ret = digp;
    } else {
	pgpDigParamsFree(digp);
    }
    return rc;
}

double JOIN_TAB::scan_time()
{
  double res;
  if (table->is_created())
  {
    if (table->is_filled_at_execution())
    {
      get_delayed_table_estimates(table, &records, &read_time,
                                    &startup_cost);
      found_records= records;
      table->quick_condition_rows= records;
    }
    else
    {
      found_records= records= table->stat_records();
      read_time= table->file->scan_time();
    }
    res= read_time;
  }
  else
  {
    found_records= records=table->stat_records();
    read_time= found_records ? (double)found_records: 10.0;
    res= read_time;
  }
  return res;
}

TfLiteRegistration CancelOpRegistration() {
  TfLiteRegistration reg = {nullptr, nullptr, nullptr, nullptr};
  reg.prepare = [](TfLiteContext* context, TfLiteNode* node) {
    const TfLiteTensor* in_tensor = GetInput(context, node, 0);
    TfLiteTensor* out_tensor = GetOutput(context, node, 0);
    TfLiteIntArray* new_size = TfLiteIntArrayCopy(in_tensor->dims);
    return context->ResizeTensor(context, out_tensor, new_size);
  };
  reg.invoke = [](TfLiteContext* context, TfLiteNode* node) {
    cancellation_data_.is_cancelled = true;
    return kTfLiteOk;
  };
  return reg;
}

static void vmx_set_constant_host_state(struct vcpu_vmx *vmx)
{
	u32 low32, high32;
	unsigned long tmpl;
	struct desc_ptr dt;
	vmcs_writel(HOST_CR0, read_cr0() & ~X86_CR0_TS);  
	vmcs_writel(HOST_CR4, read_cr4());  
	vmcs_writel(HOST_CR3, read_cr3());  
	vmcs_write16(HOST_CS_SELECTOR, __KERNEL_CS);  
#ifdef CONFIG_X86_64
	vmcs_write16(HOST_DS_SELECTOR, 0);
	vmcs_write16(HOST_ES_SELECTOR, 0);
#else
	vmcs_write16(HOST_DS_SELECTOR, __KERNEL_DS);  
	vmcs_write16(HOST_ES_SELECTOR, __KERNEL_DS);  
#endif
	vmcs_write16(HOST_SS_SELECTOR, __KERNEL_DS);  
	vmcs_write16(HOST_TR_SELECTOR, GDT_ENTRY_TSS*8);  
	native_store_idt(&dt);
	vmcs_writel(HOST_IDTR_BASE, dt.address);   
	vmx->host_idt_base = dt.address;
	vmcs_writel(HOST_RIP, vmx_return); 
	rdmsr(MSR_IA32_SYSENTER_CS, low32, high32);
	vmcs_write32(HOST_IA32_SYSENTER_CS, low32);
	rdmsrl(MSR_IA32_SYSENTER_EIP, tmpl);
	vmcs_writel(HOST_IA32_SYSENTER_EIP, tmpl);   
	if (vmcs_config.vmexit_ctrl & VM_EXIT_LOAD_IA32_PAT) {
		rdmsr(MSR_IA32_CR_PAT, low32, high32);
		vmcs_write64(HOST_IA32_PAT, low32 | ((u64) high32 << 32));
	}
}

static CACHE_BITMAP_V3_ORDER* update_read_cache_bitmap_v3_order(rdpUpdate* update, wStream* s,
                                                                UINT16 flags)
{
	BOOL rc;
	BYTE bitsPerPixelId;
	BITMAP_DATA_EX* bitmapData;
	UINT32 new_len;
	BYTE* new_data;
	CACHE_BITMAP_V3_ORDER* cache_bitmap_v3;
	if (!update || !s)
		return NULL;
	cache_bitmap_v3 = calloc(1, sizeof(CACHE_BITMAP_V3_ORDER));
	if (!cache_bitmap_v3)
		goto fail;
	cache_bitmap_v3->cacheId = flags & 0x00000003;
	cache_bitmap_v3->flags = (flags & 0x0000FF80) >> 7;
	bitsPerPixelId = (flags & 0x00000078) >> 3;
	cache_bitmap_v3->bpp = get_cbr2_bpp(bitsPerPixelId, &rc);
	if (!rc)
		goto fail;
	if (Stream_GetRemainingLength(s) < 21)
		goto fail;
	Stream_Read_UINT16(s, cache_bitmap_v3->cacheIndex); 
	Stream_Read_UINT32(s, cache_bitmap_v3->key1);       
	Stream_Read_UINT32(s, cache_bitmap_v3->key2);       
	bitmapData = &cache_bitmap_v3->bitmapData;
	Stream_Read_UINT8(s, bitmapData->bpp);
	if ((bitmapData->bpp < 1) || (bitmapData->bpp > 32))
	{
		WLog_Print(update->log, WLOG_ERROR, "invalid bpp value %" PRIu32 "", bitmapData->bpp);
		goto fail;
	}
	Stream_Seek_UINT8(s);                      
	Stream_Seek_UINT8(s);                      
	Stream_Read_UINT8(s, bitmapData->codecID); 
	Stream_Read_UINT16(s, bitmapData->width);  
	Stream_Read_UINT16(s, bitmapData->height); 
	Stream_Read_UINT32(s, new_len);            
	if ((new_len == 0) || (Stream_GetRemainingLength(s) < new_len))
		goto fail;
	new_data = (BYTE*)realloc(bitmapData->data, new_len);
	if (!new_data)
		goto fail;
	bitmapData->data = new_data;
	bitmapData->length = new_len;
	Stream_Read(s, bitmapData->data, bitmapData->length);
	return cache_bitmap_v3;
fail:
	free_cache_bitmap_v3_order(update->context, cache_bitmap_v3);
	return NULL;
}

