    }
    sprintf(filename, "%s%cenv", test_path, dir_sep);
    conffile = fopen(filename, "rb");
    if (conffile) {
        read_conf(conffile);
        fclose(conffile);
    }
    if (conf.indent < 0 || conf.indent > 255) {
        fprintf(stderr, "invalid value for JSON_INDENT: %d\n", conf.indent);
        return 2;
    }
    if (conf.indent)
        flags |= JSON_INDENT(conf.indent);
    if (conf.compact)
        flags |= JSON_COMPACT;
    if (conf.ensure_ascii)
        flags |= JSON_ENSURE_ASCII;
    if (conf.preserve_order)
        flags |= JSON_PRESERVE_ORDER;
    if (conf.sort_keys)
        flags |= JSON_SORT_KEYS;
    if (conf.have_hashseed)
        json_object_seed(conf.hashseed);
    if (conf.strip) {
        buffer = loadfile(infile);
        json = json_loads(strip(buffer), 0, &error);
        free(buffer);
    }
    else
        json = json_loadf(infile, 0, &error);
    fclose(infile);
    if (!json) {
        sprintf(errstr, "%d %d %d\n%s\n",
                error.line, error.column, error.position,
                error.text);
        ret = cmpfile(errstr, test_path, "error");
        return ret;
    }
    buffer = json_dumps(json, flags);
    ret = cmpfile(buffer, test_path, "output");
    free(buffer);
    json_decref(json);
    return ret;
}

static int __d_unalias(struct inode *inode,
		struct dentry *dentry, struct dentry *alias)
{
	struct mutex *m1 = NULL, *m2 = NULL;
	int ret = -ESTALE;
	if (alias->d_parent == dentry->d_parent)
		goto out_unalias;
	if (!mutex_trylock(&dentry->d_sb->s_vfs_rename_mutex))
		goto out_err;
	m1 = &dentry->d_sb->s_vfs_rename_mutex;
	if (!mutex_trylock(&alias->d_parent->d_inode->i_mutex))
		goto out_err;
	m2 = &alias->d_parent->d_inode->i_mutex;
out_unalias:
	__d_move(alias, dentry, false);
	ret = 0;
out_err:
	spin_unlock(&inode->i_lock);
	if (m2)
		mutex_unlock(m2);
	if (m1)
		mutex_unlock(m1);
	return ret;
}

