devzvol_create(struct vnode *dvp, char *nm, struct vattr *vap, vcexcl_t excl,
    int mode, struct vnode **vpp, struct cred *cred, int flag,
    caller_context_t *ct, vsecattr_t *vsecp)
{
	int error;
	struct vnode *vp;
	*vpp = NULL;
	error = devzvol_lookup(dvp, nm, &vp, NULL, 0, NULL, cred, ct, NULL,
	    NULL);
	if (error == 0) {
		if (excl == EXCL)
			error = EEXIST;
		else if (vp->v_type == VDIR && (mode & VWRITE))
			error = EISDIR;
		else
			error = VOP_ACCESS(vp, mode, 0, cred, ct);
		if (error) {
			VN_RELE(vp);
		} else
			*vpp = vp;
	} else if (error == ENOENT) {
		error = EROFS;
	}
	return (error);
}

static int unix_dgram_peer_wake_connect(struct sock *sk, struct sock *other)
{
	struct unix_sock *u, *u_other;
	int rc;
	u = unix_sk(sk);
	u_other = unix_sk(other);
	rc = 0;
	spin_lock(&u_other->peer_wait.lock);
	if (!u->peer_wake.private) {
		u->peer_wake.private = other;
		__add_wait_queue(&u_other->peer_wait, &u->peer_wake);
		rc = 1;
	}
	spin_unlock(&u_other->peer_wait.lock);
	return rc;
}

explicit FractionalMaxPoolOp(OpKernelConstruction* context)
    : OpKernel(context) {
  OP_REQUIRES_OK(context, context->GetAttr("pooling_ratio", &pooling_ratio_));
  OP_REQUIRES_OK(context, context->GetAttr("pseudo_random", &pseudo_random_));
  OP_REQUIRES_OK(context, context->GetAttr("overlapping", &overlapping_));
  OP_REQUIRES(context, pooling_ratio_.size() == 4,
              errors::InvalidArgument("pooling_ratio field must "
                                      "specify 4 dimensions"));
  for (std::size_t i = 0; i < pooling_ratio_.size(); ++i) {
    OP_REQUIRES(context, pooling_ratio_[i] >= 1,
                errors::InvalidArgument(
                    "pooling_ratio cannot be smaller than 1, got: ",
                    pooling_ratio_[i]));
  }
  OP_REQUIRES(
      context, pooling_ratio_[0] == 1 || pooling_ratio_[3] == 1,
      errors::Unimplemented("Fractional max pooling is not yet "
                            "supported on the batch nor channel dimension."));
  OP_REQUIRES_OK(context, context->GetAttr("deterministic", &deterministic_));
  OP_REQUIRES_OK(context, context->GetAttr("seed", &seed_));
  OP_REQUIRES_OK(context, context->GetAttr("seed2", &seed2_));
  if (deterministic_) {
    if ((seed_ == 0) && (seed2_ == 0)) {
      seed_ = random::New64();
      seed2_ = random::New64();
    }
  } else {
    OP_REQUIRES(
        context, (seed_ == 0) && (seed2_ == 0),
        errors::InvalidArgument(
            "Both seed and seed2 should be 0 if deterministic is false."));
  }
}

BOOL license_read_scope_list(wStream* s, SCOPE_LIST* scopeList)
{
	UINT32 i;
	UINT32 scopeCount;
	if (Stream_GetRemainingLength(s) < 4)
		return FALSE;
	Stream_Read_UINT32(s, scopeCount); 
        if (Stream_GetRemainingLength(s) / sizeof(LICENSE_BLOB) < scopeCount)
                return FALSE;  
	scopeList->count = scopeCount;
	scopeList->array = (LICENSE_BLOB*) malloc(sizeof(LICENSE_BLOB) * scopeCount);
	for (i = 0; i < scopeCount; i++)
	{
		scopeList->array[i].type = BB_SCOPE_BLOB;
		if (!license_read_binary_blob(s, &scopeList->array[i]))
			return FALSE;
	}
	return TRUE;
}

static int unix_dgram_peer_wake_relay(wait_queue_t *q, unsigned mode, int flags,
				      void *key)
{
	struct unix_sock *u;
	wait_queue_head_t *u_sleep;
	u = container_of(q, struct unix_sock, peer_wake);
	__remove_wait_queue(&unix_sk(u->peer_wake.private)->peer_wait,
			    q);
	u->peer_wake.private = NULL;
	u_sleep = sk_sleep(&u->sk);
	if (u_sleep)
		wake_up_interruptible_poll(u_sleep, key);
	return 0;
}

TfLiteStatus Eval(TfLiteContext* context, TfLiteNode* node) {
  const TfLiteTensor* input = GetInput(context, node, kInput);
  const TfLiteTensor* input_state = GetInput(context, node, kInputState);
  const TfLiteTensor* gate_weight = GetInput(context, node, kGateWeight);
  const TfLiteTensor* gate_bias = GetInput(context, node, kGateBias);
  const TfLiteTensor* candidate_weight =
      GetInput(context, node, kCandidateWeight);
  const TfLiteTensor* candidate_bias = GetInput(context, node, kCandidateBias);
  TfLiteTensor* output = GetOutput(context, node, kOutput);
  TfLiteTensor* output_state = GetOutput(context, node, kOutputState);
  TfLiteTensor* activation = GetTemporary(context, node, kActivation);
  TfLiteTensor* concat = GetTemporary(context, node, kConcat);
  auto cpu_backend_context = CpuBackendContext::GetFromContext(context);
  if (gate_weight->type == kTfLiteFloat32) {
    GruImpl(input, input_state, gate_weight, gate_bias, candidate_weight,
            candidate_bias, output, output_state, activation, concat,
            cpu_backend_context);
  } else {
    context->ReportError(context,
                         "Unsupported combination of data types for GruCell");
    return kTfLiteError;
  }
  return kTfLiteOk;
}

