Status CalculateOutputIndex(OpKernelContext* context, int dimension,
                            const vector<INDEX_TYPE>& parent_output_index,
                            INDEX_TYPE output_index_multiplier,
                            INDEX_TYPE output_size,
                            vector<INDEX_TYPE>* result) {
  const RowPartitionTensor row_partition_tensor =
      GetRowPartitionTensor(context, dimension);
  auto partition_type = GetRowPartitionTypeByDimension(dimension);
  switch (partition_type) {
    case RowPartitionType::VALUE_ROWIDS:
      CalculateOutputIndexValueRowID(
          context, row_partition_tensor, parent_output_index,
          output_index_multiplier, output_size, result);
      return tensorflow::Status::OK();
    case RowPartitionType::ROW_SPLITS:
      if (row_partition_tensor.size() - 1 > parent_output_index.size()) {
        return errors::InvalidArgument(
            "Row partition size is greater than output size: ",
            row_partition_tensor.size() - 1, " > ",
            parent_output_index.size());
      }
      CalculateOutputIndexRowSplit(
          context, row_partition_tensor, parent_output_index,
          output_index_multiplier, output_size, result);
      return tensorflow::Status::OK();
    default:
      return errors::InvalidArgument(
          "Unsupported partition type:",
          RowPartitionTypeToString(partition_type));
  }
}

R_API int r_socket_read_block(RSocket *s, ut8 *buf, int len) {
	int ret = 0;
	for (ret = 0; ret < len; ) {
		int r = r_socket_read (s, buf + ret, len - ret);
		if (r == -1) {
#if HAVE_LIB_SSL
			if (SSL_get_error (s->sfd, r) == SSL_ERROR_WANT_READ) {
				if (r_socket_ready (s, 1, 0) == 1) {
					continue;
				}
			}
#endif
			return -1;
		}
		if (r < 1) {
			break;
		}
		ret += r;
	}
	return ret;
}

BGD_DECLARE(void) gdImageWebpCtx (gdImagePtr im, gdIOCtx * outfile, int quality)
{
	uint8_t *argb;
	int x, y;
	uint8_t *p;
	uint8_t *out;
	size_t out_size;
	if (im == NULL) {
		return;
	}
	if (!gdImageTrueColor(im)) {
		gd_error("Paletter image not supported by webp");
		return;
	}
	if (quality == -1) {
		quality = 80;
	}
	if (overflow2(gdImageSX(im), 4)) {
		return;
	}
	if (overflow2(gdImageSX(im) * 4, gdImageSY(im))) {
		return;
	}
	argb = (uint8_t *)gdMalloc(gdImageSX(im) * 4 * gdImageSY(im));
	if (!argb) {
		return;
	}
	p = argb;
	for (y = 0; y < gdImageSY(im); y++) {
		for (x = 0; x < gdImageSX(im); x++) {
			register int c;
			register char a;
			c = im->tpixels[y][x];
			a = gdTrueColorGetAlpha(c);
			if (a == 127) {
				a = 0;
			} else {
				a = 255 - ((a << 1) + (a >> 6));
			}
			*(p++) = gdTrueColorGetRed(c);
			*(p++) = gdTrueColorGetGreen(c);
			*(p++) = gdTrueColorGetBlue(c); 
			*(p++) = a;
		}
	}
	out_size = WebPEncodeRGBA(argb, gdImageSX(im), gdImageSY(im), gdImageSX(im) * 4, quality, &out);
	if (out_size == 0) {
		gd_error("gd-webp encoding failed");
		goto freeargb;
	}
	gdPutBuf(out, out_size, outfile);
	free(out);
freeargb:
	gdFree(argb);
}

static void copy_fields(const FieldMatchContext *fm, AVFrame *dst,
