	suite = xml_load_suite();
	runner = srunner_create(suite);
	srunner_run_all(runner, CK_NORMAL);
	tests_failed = srunner_ntests_failed(runner);
	srunner_free(runner);
	return (tests_failed == 0) ? EXIT_SUCCESS : EXIT_FAILURE;
}

