static void Launch(OpKernelContext* context, const Tensor& in_x,
                   const Tensor& in_y, bool adjoint, bool lower,
                   const MatMulBCast& bcast, Tensor* out) {
  const int64 batch_size = bcast.output_batch_size();
  const int64 cost_per_unit =
      in_x.dim_size(1) * in_x.dim_size(2) * in_y.dim_size(2);
  auto worker_threads = *(context->device()->tensorflow_cpu_worker_threads());
  using Matrix =
      Eigen::Matrix<Scalar, Eigen::Dynamic, Eigen::Dynamic, Eigen::RowMajor>;
  using ConstMatrixMap = Eigen::Map<const Matrix>;
  using RealScalar = typename Eigen::NumTraits<Scalar>::Real;
  auto matrix = ConstMatrixMap(in_x.flat<Scalar>().data(), in_x.dim_size(1),
                               in_x.dim_size(2));
  RealScalar min_abs_pivot;
  if (lower) {
    min_abs_pivot = matrix.row(0).cwiseAbs().minCoeff();
  } else {
    min_abs_pivot = matrix.row(in_x.dim_size(1) - 1).cwiseAbs().minCoeff();
  }
  OP_REQUIRES(context, min_abs_pivot > RealScalar(0),
              errors::InvalidArgument("Input matrix is not invertible."));
  Shard(worker_threads.num_threads, worker_threads.workers, batch_size,
        cost_per_unit,
        [&in_x, &in_y, adjoint, lower, &bcast, out](int start, int limit) {
          SequentialBandedTriangularSolveKernel<Scalar>::Run(
              in_x, in_y, lower, adjoint, bcast, out, start, limit);
        });
}

storageDriverAutostartCallback(virStoragePoolObj *obj,
                               const void *opaque G_GNUC_UNUSED)
{
    virStoragePoolDef *def = virStoragePoolObjGetDef(obj);
    virStorageBackend *backend;
    bool started = false;
    if (!(backend = virStorageBackendForType(def->type)))
        return;
    if (virStoragePoolObjIsAutostart(obj) &&
        !virStoragePoolObjIsActive(obj)) {
        virStoragePoolObjSetStarting(obj, true);
        if (backend->startPool &&
            backend->startPool(obj) < 0) {
            virReportError(VIR_ERR_INTERNAL_ERROR,
                           _("Failed to autostart storage pool '%s': %s"),
                           def->name, virGetLastErrorMessage());
            goto cleanup;
        }
        started = true;
    }
    if (started) {
        g_autofree char *stateFile = NULL;
        stateFile = virFileBuildPath(driver->stateDir, def->name, ".xml");
        if (!stateFile ||
            virStoragePoolSaveState(stateFile, def) < 0 ||
            storagePoolRefreshImpl(backend, obj, stateFile) < 0) {
            virReportError(VIR_ERR_INTERNAL_ERROR,
                           _("Failed to autostart storage pool '%s': %s"),
                           def->name, virGetLastErrorMessage());
        } else {
            virStoragePoolObjSetActive(obj, true);
        }
    }
 cleanup:
    if (virStoragePoolObjIsStarting(obj)) {
        if (!virStoragePoolObjIsActive(obj))
            virStoragePoolUpdateInactive(obj);
        virStoragePoolObjSetStarting(obj, false);
    }
}

static int _gdImageWBMPCtx(gdImagePtr image, int fg, gdIOCtx *out)
{
	int x, y, pos;
	Wbmp *wbmp;
	if((wbmp = createwbmp(gdImageSX(image), gdImageSY(image), WBMP_WHITE)) == NULL) {
		gd_error("Could not create WBMP\n");
		return 1;
	}
	pos = 0;
	for(y = 0; y < gdImageSY(image); y++) {
		for(x = 0; x < gdImageSX(image); x++) {
			if(gdImageGetPixel(image, x, y) == fg) {
				wbmp->bitmap[pos] = WBMP_BLACK;
			}
			pos++;
		}
	}
	if(writewbmp(wbmp, &gd_putout, out)) {
		freewbmp(wbmp);
		gd_error("Could not save WBMP\n");
		return 1;
	}
	freewbmp(wbmp);
	return 0;
}

static BOOL nsc_rle_decode(BYTE* in, BYTE* out, UINT32 outSize, UINT32 originalSize)
{
	UINT32 left = originalSize;
	while (left > 4)
	{
		const BYTE value = *in++;
		UINT32 len = 0;
		if (left == 5)
		{
			if (outSize < 1)
				return FALSE;
			outSize--;
			*out++ = value;
			left--;
		}
		else if (value == *in)
		{
			in++;
			if (*in < 0xFF)
			{
				len = (UINT32)*in++;
				len += 2;
			}
			else
			{
				in++;
				len = ((UINT32)(*in++));
				len |= ((UINT32)(*in++)) << 8U;
				len |= ((UINT32)(*in++)) << 16U;
				len |= ((UINT32)(*in++)) << 24U;
			}
			if (outSize < len)
				return FALSE;
			outSize -= len;
			FillMemory(out, len, value);
			out += len;
			left -= len;
		}
		else
		{
			if (outSize < 1)
				return FALSE;
			outSize--;
			*out++ = value;
			left--;
		}
	}
	if ((outSize < 4) || (left < 4))
		return FALSE;
	memcpy(out, in, 4);
	return TRUE;
}

static int multipath_ioctl(struct dm_target *ti, unsigned int cmd,
			   unsigned long arg)
{
	struct multipath *m = (struct multipath *) ti->private;
	struct block_device *bdev = NULL;
	fmode_t mode = 0;
	unsigned long flags;
	int r = 0;
	spin_lock_irqsave(&m->lock, flags);
	if (!m->current_pgpath)
		__choose_pgpath(m, 0);
	if (m->current_pgpath) {
		bdev = m->current_pgpath->path.dev->bdev;
		mode = m->current_pgpath->path.dev->mode;
	}
	if (m->queue_io)
		r = -EAGAIN;
	else if (!bdev)
		r = -EIO;
	spin_unlock_irqrestore(&m->lock, flags);
	if (!r && ti->len != i_size_read(bdev->bd_inode) >> SECTOR_SHIFT)
		r = scsi_verify_blk_ioctl(NULL, cmd);
	return r ? : __blkdev_driver_ioctl(bdev, mode, cmd, arg);
}

