ip_optprint(netdissect_options *ndo,
            register const u_char *cp, u_int length)
{
	register u_int option_len;
	const char *sep = "";
	for (; length > 0; cp += option_len, length -= option_len) {
		u_int option_code;
		ND_PRINT((ndo, "%s", sep));
		sep = ",";
		ND_TCHECK(*cp);
		option_code = *cp;
		ND_PRINT((ndo, "%s",
		          tok2str(ip_option_values,"unknown %u",option_code)));
		if (option_code == IPOPT_NOP ||
                    option_code == IPOPT_EOL)
			option_len = 1;
		else {
			ND_TCHECK(cp[1]);
			option_len = cp[1];
			if (option_len < 2) {
				ND_PRINT((ndo, " [bad length %u]", option_len));
				return;
			}
		}
		if (option_len > length) {
			ND_PRINT((ndo, " [bad length %u]", option_len));
			return;
		}
		ND_TCHECK2(*cp, option_len);
		switch (option_code) {
		case IPOPT_EOL:
			return;
		case IPOPT_TS:
			if (ip_printts(ndo, cp, option_len) == -1)
				goto trunc;
			break;
		case IPOPT_RR:       
		case IPOPT_SSRR:
		case IPOPT_LSRR:
			if (ip_printroute(ndo, cp, option_len) == -1)
				goto trunc;
			break;
		case IPOPT_RA:
			if (option_len < 4) {
				ND_PRINT((ndo, " [bad length %u]", option_len));
				break;
			}
			ND_TCHECK(cp[3]);
			if (EXTRACT_16BITS(&cp[2]) != 0)
				ND_PRINT((ndo, " value %u", EXTRACT_16BITS(&cp[2])));
			break;
		case IPOPT_NOP:       
		case IPOPT_SECURITY:
		default:
			break;
		}
	}
	return;
trunc:
	ND_PRINT((ndo, "%s", tstr));
}

int sub__messages_queue(const char *source_id, const char *topic, uint8_t qos, int retain, struct mosquitto_msg_store **stored)
{
	int rc = MOSQ_ERR_SUCCESS, rc2;
	struct mosquitto__subhier *subhier;
	char **split_topics = NULL;
	char *local_topic = NULL;
	assert(topic);
	if(sub__topic_tokenise(topic, &local_topic, &split_topics, NULL)) return 1;
	db__msg_store_ref_inc(*stored);
	HASH_FIND(hh, db.subs, split_topics[0], strlen(split_topics[0]), subhier);
	if(subhier){
		rc = sub__search(subhier, split_topics, source_id, topic, qos, retain, *stored);
	}
	if(retain){
		rc2 = retain__store(topic, *stored, split_topics);
		if(rc2) rc = rc2;
	}
	mosquitto__free(split_topics);
	mosquitto__free(local_topic);
	db__msg_store_ref_dec(stored);
	return rc;
}

void luaT_adjustvarargs (lua_State *L, int nfixparams, CallInfo *ci,
                         const Proto *p) {
  int i;
  int actual = cast_int(L->top - ci->func) - 1;  
  int nextra = actual - nfixparams;  
  ci->u.l.nextraargs = nextra;
  checkstackGC(L, p->maxstacksize + 1);
  setobjs2s(L, L->top++, ci->func);
  for (i = 1; i <= nfixparams; i++) {
    setobjs2s(L, L->top++, ci->func + i);
    setnilvalue(s2v(ci->func + i));  
  }
  ci->func += actual + 1;
  ci->top += actual + 1;
  lua_assert(L->top <= ci->top && ci->top <= L->stack_last);
}

