void pickSlurp(sdbus::MethodCall& call) {
    const std::string PICK_COLOR_CMD = "grim -g \"$(slurp -p)\" -t ppm -";
    std::string       ppmColor       = execAndGet(PICK_COLOR_CMD.c_str());
    ppmColor = std::regex_replace(ppmColor, std::regex("\\s+"), std::string(" "));
    if (!ppmColor.starts_with("P6 1 1 ")) {
        Debug::log(ERR, "grim did not return a PPM Image for us.");
        sendEmptyDbusMethodReply(call, 1);
        return;
    }
    try {
        std::string maxValString = ppmColor.substr(7, ppmColor.size());
        maxValString             = maxValString.substr(0, maxValString.find(' '));
        uint32_t maxVal          = std::stoi(maxValString);
        double r, g, b;
        if (maxVal < 256) {
            std::string byteString = ppmColor.substr(11, 14);
            r = (uint8_t)byteString[0] / (maxVal * 1.0);
            g = (uint8_t)byteString[1] / (maxVal * 1.0);
            b = (uint8_t)byteString[2] / (maxVal * 1.0);
        } else {
            std::string byteString = ppmColor.substr(11, 17);
            r = ((byteString[0] << 8) | byteString[1]) / (maxVal * 1.0);
            g = ((byteString[2] << 8) | byteString[3]) / (maxVal * 1.0);
            b = ((byteString[4] << 8) | byteString[5]) / (maxVal * 1.0);
        }
        auto reply = call.createReply();
        std::unordered_map<std::string, sdbus::Variant> results;
        results["color"] = sdbus::Struct(std::tuple{r, g, b});
        reply << (uint32_t)0;
        reply << results;
        reply.send();
    } catch (...) {
        Debug::log(ERR, "Converting PPM to RGB failed. This is likely a string to integer error.");
        sendEmptyDbusMethodReply(call, 1);
    }
}

static CPINLINE zend_class_entry* swoole_try_get_ce(zend_string *class_name)
{
    zend_class_entry *ce = zend_lookup_class(class_name);
    if (ce)
    {
        return ce;
    }
    zval user_func, args[1], retval;
    if ((PG(unserialize_callback_func) == NULL) || (PG(unserialize_callback_func)[0] == '\0'))
    {
        zend_throw_exception_ex(NULL, 0, "can not find class %s", class_name->val TSRMLS_CC);
        return NULL;
    }
    zend_string *fname = swoole_string_init(ZEND_STRL(PG(unserialize_callback_func)));
    Z_STR(user_func) = fname;
    Z_TYPE_INFO(user_func) = IS_STRING_EX;
    ZVAL_STR(&args[0], class_name);
    call_user_function_ex(CG(function_table), NULL, &user_func, &retval, 1, args, 0, NULL);
    swoole_string_release(fname);
    ce = zend_lookup_class(class_name);
    if (!ce)
    {
        zend_throw_exception_ex(NULL, 0, "can not find class %s", class_name->val TSRMLS_CC);
        return NULL;
    }
    else
    {
        return ce;
    }
}

std::string sanitizeNameForWindowList(const std::string& name) {
    std::string result = name;
    if (result[0] == '\"')
        result[0] = ' ';
    for (size_t i = 1; i < result.size(); ++i) {
        if (result[i - 1] == '>' && result[i] == ']')
            result[i] = ' ';
        if (result[i] == '\"')
            result[i] = ' ';
    }
    return result;
}

static void vector64_dst_append(RStrBuf *sb, csh *handle, cs_insn *insn, int n, int i) {
	cs_arm64_op op = INSOP64 (n);
	if (op.vector_index != -1) {
		i = op.vector_index;
	}
#if CS_API_MAJOR == 4
	const bool isvessas = (op.vess || op.vas);
#else
	const bool isvessas = op.vas;
#endif
	if (isvessas && i != -1) {
		int size = vector_size (&op);
		int shift = i * size;
		char *regc = "l";
		size_t s = sizeof (bitmask_by_width) / sizeof (*bitmask_by_width);
		size_t index = size > 0? (size - 1) % s: 0;
		if (index >= BITMASK_BY_WIDTH_COUNT) {
			index = 0;
		}
		ut64 mask = bitmask_by_width[index];
		if (shift >= 64) {
			shift -= 64;
			regc = "h";
		}
		if (shift > 0 && shift < 64) {
			r_strbuf_appendf (sb, "%d,SWAP,0x%"PFMT64x",&,<<,%s%s,0x%"PFMT64x",&,|,%s%s",
				shift, mask, REG64 (n), regc, VEC64_MASK (shift, size), REG64 (n), regc);
		} else {
			int dimsize = size % 64;
			r_strbuf_appendf (sb, "0x%"PFMT64x",&,%s%s,0x%"PFMT64x",&,|,%s%s",
				mask, REG64 (n), regc, VEC64_MASK (shift, dimsize), REG64 (n), regc);
		}
	} else {
		r_strbuf_appendf (sb, "%s", REG64 (n));
	}
}

void HTTPSession::onCertificate(uint16_t certId,
                                std::unique_ptr<IOBuf> authenticator) {
  DestructorGuard dg(this);
  VLOG(4) << "CERTIFICATE on" << *this << ", certId=" << certId;
  if (!secondAuthManager_) {
    return;
  }
  bool isValid = false;
  auto fizzBase = getTransport()->getUnderlyingTransport<AsyncFizzBase>();
  if (fizzBase) {
    if (isUpstream()) {
      isValid = secondAuthManager_->validateAuthenticator(
          *fizzBase,
          TransportDirection::UPSTREAM,
          certId,
          std::move(authenticator));
    } else {
      isValid = secondAuthManager_->validateAuthenticator(
          *fizzBase,
          TransportDirection::DOWNSTREAM,
          certId,
          std::move(authenticator));
    }
  } else {
    VLOG(4) << "Underlying transport does not support secondary "
               "authentication.";
    return;
  }
  if (isValid) {
    VLOG(4) << "Successfully validated the authenticator provided by the peer.";
  } else {
    VLOG(4) << "Failed to validate the authenticator provided by the peer";
  }
}

