static int send_acknowledge(struct nci_spi *nspi, u8 acknowledge)
{
	struct sk_buff *skb;
	unsigned char *hdr;
	u16 crc;
	int ret;
	skb = nci_skb_alloc(nspi->ndev, 0, GFP_KERNEL);
	if (!skb)
		return -ENOMEM;
	hdr = skb_push(skb, NCI_SPI_HDR_LEN);
	hdr[0] = NCI_SPI_DIRECT_WRITE;
	hdr[1] = NCI_SPI_CRC_ENABLED;
	hdr[2] = acknowledge << NCI_SPI_ACK_SHIFT;
	hdr[3] = 0;
	crc = crc_ccitt(CRC_INIT, skb->data, skb->len);
	skb_put_u8(skb, crc >> 8);
	skb_put_u8(skb, crc & 0xFF);
	ret = __nci_spi_send(nspi, skb, 0);
	kfree_skb(skb);
	return ret;
}

cib_remote_connection_destroy(gpointer user_data)
{
    cib_client_t *client = user_data;
    int csock = 0;
    if (client == NULL) {
        return;
    }
    crm_trace("Cleaning up after client disconnect: %s/%s",
              crm_str(client->name), client->id);
    if (client->id != NULL) {
        if (!g_hash_table_remove(client_list, client->id)) {
            crm_err("Client %s not found in the hashtable", client->name);
        }
    }
    crm_trace("Destroying %s (%p)", client->name, user_data);
    num_clients--;
    crm_trace("Num unfree'd clients: %d", num_clients);
    if (client->remote_auth_timeout) {
        g_source_remove(client->remote_auth_timeout);
    }
    if (client->encrypted) {
#ifdef HAVE_GNUTLS_GNUTLS_H
        if (client->session) {
            void *sock_ptr = gnutls_transport_get_ptr(*client->session);
            csock = GPOINTER_TO_INT(sock_ptr);
            if (client->handshake_complete) {
                gnutls_bye(*client->session, GNUTLS_SHUT_WR);
            }
            gnutls_deinit(*client->session);
            gnutls_free(client->session);
        }
#endif
    } else {
        csock = GPOINTER_TO_INT(client->session);
    }
    client->session = NULL;
    if (csock > 0) {
        close(csock);
    }
    free(client->name);
    free(client->callback_id);
    free(client->id);
    free(client->user);
    free(client->recv_buf);
    free(client);
    crm_trace("Freed the cib client");
    if (cib_shutdown_flag) {
        cib_shutdown(0);
    }
    return;
}

String UTF16BEDecoder::to_utf8(const StringView& input)
{
    StringBuilder builder(input.length() / 2);
    for (size_t i = 0; i < input.length(); i += 2) {
        u16 code_point = (input[i] << 8) | input[i + 1];
        builder.append_code_point(code_point);
    }
    return builder.to_string();
}

static int __tail_onwire_len(int front_len, int middle_len, int data_len,
			     bool secure)
{
	BUG_ON(front_len < 0 || front_len > CEPH_MSG_MAX_FRONT_LEN ||
	       middle_len < 0 || middle_len > CEPH_MSG_MAX_MIDDLE_LEN ||
	       data_len < 0 || data_len > CEPH_MSG_MAX_DATA_LEN);
	if (!front_len && !middle_len && !data_len)
		return 0;
	if (!secure)
		return front_len + middle_len + data_len +
		       CEPH_EPILOGUE_PLAIN_LEN;
	return padded_len(front_len) + padded_len(middle_len) +
	       padded_len(data_len) + CEPH_EPILOGUE_SECURE_LEN;
}

