	dprintk("%s: write %Zd bytes\n", bd->name, count);
	if (unlikely(segment_eq(get_fs(), KERNEL_DS)))
		return -EINVAL;
	bsg_set_block(bd, file);
	bytes_written = 0;
	ret = __bsg_write(bd, buf, count, &bytes_written,
