	return NULL;
}

void ValidateOpDimensionsFromInputs(const int n, const int h, const int w,
                                    const int c, const int kx, const int ky,
                                    const int sx, const int sy,
                                    const string& data_format,
                                    const string& padding) {
  OpContext op_context;
  int ho;
  int wo;
  if (data_format == "NHWC") {
    op_context = DescribePoolingOp("MaxPool", {n, h, w, c}, {1, kx, ky, 1},
                                   {1, sx, sy, 1}, "NHWC", padding);
    ho = op_context.op_info.outputs(0).shape().dim(1).size();
    wo = op_context.op_info.outputs(0).shape().dim(2).size();
  } else {
    op_context = DescribePoolingOp("MaxPool", {n, c, h, w}, {1, 1, kx, ky},
                                   {1, 1, sx, sy}, "NCHW", padding);
    ho = op_context.op_info.outputs(0).shape().dim(2).size();
    wo = op_context.op_info.outputs(0).shape().dim(3).size();
  }
  bool found_unknown_shapes;
  auto dims = OpLevelCostEstimator::OpDimensionsFromInputs(
      op_context.op_info.inputs(0).shape(), op_context.op_info,
      &found_unknown_shapes);
  Padding padding_enum;
  if (padding == "VALID") {
    padding_enum = Padding::VALID;
  } else {
    padding_enum = Padding::SAME;
  }
  EXPECT_EQ(n, dims.batch);
  EXPECT_EQ(h, dims.ix);
  EXPECT_EQ(w, dims.iy);
  EXPECT_EQ(c, dims.iz);
  EXPECT_EQ(kx, dims.kx);
  EXPECT_EQ(ky, dims.ky);
  EXPECT_EQ(sx, dims.sx);
  EXPECT_EQ(sy, dims.sy);
  EXPECT_EQ(ho, dims.ox);
  EXPECT_EQ(wo, dims.oy);
  EXPECT_EQ(c, dims.oz);
  EXPECT_EQ(padding_enum, dims.padding);
}

QPDFWriter::enqueueObject(QPDFObjectHandle object)
{
    if (object.isIndirect())
    {
        if (object.getOwningQPDF() != &(this->pdf))
        {
            QTC::TC("qpdf", "QPDFWriter foreign object");
            throw std::logic_error(
                "QPDFObjectHandle from different QPDF found while writing."
                "  Use QPDF::copyForeignObject to add objects from"
                " another file.");
        }
	QPDFObjGen og = object.getObjGen();
	if (obj_renumber.count(og) == 0)
	{
	    if (this->object_to_object_stream.count(og))
	    {
		int stream_id = this->object_to_object_stream[og];
                obj_renumber[og] = 0;
		enqueueObject(this->pdf.getObjectByID(stream_id, 0));
	    }
	    else
	    {
		object_queue.push_back(object);
		obj_renumber[og] = next_objid++;
		if ((og.getGen() == 0) &&
                    this->object_stream_to_objects.count(og.getObj()))
		{
		    if (! this->linearized)
		    {
			assignCompressedObjectNumbers(og);
		    }
		}
		else if ((! this->direct_stream_lengths) && object.isStream())
		{
		    ++next_objid;
		}
	    }
	}
        else if (obj_renumber[og] == 0)
        {
            QTC::TC("qpdf", "QPDFWriter ignore self-referential object stream");
        }
    }
    else if (object.isArray())
    {
	int n = object.getArrayNItems();
	for (int i = 0; i < n; ++i)
	{
	    if (! this->linearized)
	    {
		enqueueObject(object.getArrayItem(i));
	    }
	}
    }
    else if (object.isDictionary())
    {
	std::set<std::string> keys = object.getKeys();
	for (std::set<std::string>::iterator iter = keys.begin();
	     iter != keys.end(); ++iter)
	{
	    if (! this->linearized)
	    {
		enqueueObject(object.getKey(*iter));
	    }
	}
    }
    else
    {
    }
}

