SHA256_Pad(struct SHA256_CTX * ctx)
{
  unsigned char len[8];
  uint32_t r, plen;
  be32enc_vect(len, ctx->count, 8);
  r = (ctx->count[1] >> 3) & 0x3f;
  plen = (r < 56) ? (56 - r) : (120 - r);
  scrypt_SHA256_Update(ctx, PAD, (size_t)plen);
  scrypt_SHA256_Update(ctx, len, 8);
}

