HMAC_SHA256_Init(struct HMAC_SHA256_CTX * ctx, const void * _K, size_t Klen)
{
  unsigned char pad[64];
  unsigned char khash[32];
  const unsigned char * K = (unsigned char *) _K;
  size_t i;
  if (Klen > 64) {
    SHA256_Init(&ctx->ictx);
    scrypt_SHA256_Update(&ctx->ictx, K, Klen);
    scrypt_SHA256_Final(khash, &ctx->ictx);
    K = khash;
    Klen = 32;
  }
  SHA256_Init(&ctx->ictx);
  memset(pad, 0x36, 64);
  for (i = 0; i < Klen; i++)
    pad[i] ^= K[i];
  scrypt_SHA256_Update(&ctx->ictx, pad, 64);
  SHA256_Init(&ctx->octx);
  memset(pad, 0x5c, 64);
  for (i = 0; i < Klen; i++)
    pad[i] ^= K[i];
  scrypt_SHA256_Update(&ctx->octx, pad, 64);
  memset(khash, 0, 32);
}

void Filter::onUpstreamEvent(Network::ConnectionEvent event) {
  bool connecting = connecting_;
  connecting_ = false;
  if (event == Network::ConnectionEvent::RemoteClose ||
      event == Network::ConnectionEvent::LocalClose) {
    upstream_.reset();
    disableIdleTimer();
    if (connecting) {
      if (event == Network::ConnectionEvent::RemoteClose) {
        getStreamInfo().setResponseFlag(StreamInfo::ResponseFlag::UpstreamConnectionFailure);
        read_callbacks_->upstreamHost()->outlierDetector().putResult(
            Upstream::Outlier::Result::LocalOriginConnectFailed);
      }
      initializeUpstreamConnection();
    } else {
      if (read_callbacks_->connection().state() == Network::Connection::State::Open) {
        read_callbacks_->connection().close(Network::ConnectionCloseType::FlushWrite);
      }
    }
  }
}

