	debug("publish: saving content(%zu): %b\n",
		  clen, mbuf_buf(mb), mbuf_get_left(mb));
	mbuf_strdup(mb, &ie->content.publish, clen);
	http_reply(conn, 200, "OK", NULL);
	http_conn_close(conn);
 out:
	mem_deref(key);
}

