absl::Status IsSupported(const TfLiteContext* context,
                         const TfLiteNode* tflite_node,
                         const TfLiteRegistration* registration) final {
  RETURN_IF_ERROR(CheckMaxSupportedOpVersion(registration, 3));
  if (tflite_node->inputs->size != 2) {
    return absl::UnimplementedError("MUL requires two input tensors.");
  }
  auto input0 = tflite::GetInput(context, tflite_node, 0);
  auto input1 = tflite::GetInput(context, tflite_node, 1);
  if (input0 == nullptr || input1 == nullptr) {
    return absl::InvalidArgumentError("At least one input tensor is null");
  }
  if (input0->dims->size == input1->dims->size) {
    bool first_has_smaller_dim = false;
    bool second_has_smaller_dim = false;
    for (int i = 0; i < input0->dims->size; ++i) {
      if (input0->dims->data[i] < input1->dims->data[i]) {
        first_has_smaller_dim = true;
      }
      if (input1->dims->data[i] < input0->dims->data[i]) {
        second_has_smaller_dim = true;
      }
    }
    if (first_has_smaller_dim && second_has_smaller_dim) {
      return absl::UnimplementedError(
          "MUL requires one tensor that not less than second in all "
          "dimensions.");
    }
  }
  const TfLiteMulParams* tf_options;
  RETURN_IF_ERROR(RetrieveBuiltinData(tflite_node, &tf_options));
  return IsActivationSupported(tf_options->activation);
}

static void draw_block_rectangle(uint8_t *buf, int sx, int sy, int w, int h, ptrdiff_t stride, int color)
{
    for (int x = sx; x < sx + w; x++)
        buf[x] = color;
    for (int y = sy; y < sy + h; y++) {
        buf[sx] = color;
        buf[sx + w - 1] = color;
        buf += stride;
    }
    for (int x = sx; x < sx + w; x++)
        buf[x] = color;
}

static int rtnl_bridge_setlink(struct sk_buff *skb, struct nlmsghdr *nlh)
{
	struct net *net = sock_net(skb->sk);
	struct ifinfomsg *ifm;
	struct net_device *dev;
	struct nlattr *br_spec, *attr = NULL;
	int rem, err = -EOPNOTSUPP;
	u16 flags = 0;
	bool have_flags = false;
	if (nlmsg_len(nlh) < sizeof(*ifm))
		return -EINVAL;
	ifm = nlmsg_data(nlh);
	if (ifm->ifi_family != AF_BRIDGE)
		return -EPFNOSUPPORT;
	dev = __dev_get_by_index(net, ifm->ifi_index);
	if (!dev) {
		pr_info("PF_BRIDGE: RTM_SETLINK with unknown ifindex\n");
		return -ENODEV;
	}
	br_spec = nlmsg_find_attr(nlh, sizeof(struct ifinfomsg), IFLA_AF_SPEC);
	if (br_spec) {
		nla_for_each_nested(attr, br_spec, rem) {
			if (nla_type(attr) == IFLA_BRIDGE_FLAGS) {
				if (nla_len(attr) < sizeof(flags))
					return -EINVAL;
				have_flags = true;
				flags = nla_get_u16(attr);
				break;
			}
		}
	}
	if (!flags || (flags & BRIDGE_FLAGS_MASTER)) {
		struct net_device *br_dev = netdev_master_upper_dev_get(dev);
		if (!br_dev || !br_dev->netdev_ops->ndo_bridge_setlink) {
			err = -EOPNOTSUPP;
			goto out;
		}
		err = br_dev->netdev_ops->ndo_bridge_setlink(dev, nlh, flags);
		if (err)
			goto out;
		flags &= ~BRIDGE_FLAGS_MASTER;
	}
	if ((flags & BRIDGE_FLAGS_SELF)) {
		if (!dev->netdev_ops->ndo_bridge_setlink)
			err = -EOPNOTSUPP;
		else
			err = dev->netdev_ops->ndo_bridge_setlink(dev, nlh,
								  flags);
		if (!err) {
			flags &= ~BRIDGE_FLAGS_SELF;
			err = rtnl_bridge_notify(dev);
		}
	}
	if (have_flags)
		memcpy(nla_data(attr), &flags, sizeof(flags));
out:
	return err;
}

void AcceptRoutingHandler<Pipeline, R>::onRoutingData(
    uint64_t connId,
    typename RoutingDataHandler<R>::RoutingData& routingData) {
  auto routingPipelineIter = routingPipelines_.find(connId);
  if (routingPipelineIter == routingPipelines_.end()) {
    VLOG(2) << "Connection has already been closed, "
               "or routed to a worker thread.";
    return;
  }
  auto routingPipeline = std::move(routingPipelineIter->second);
  routingPipelines_.erase(routingPipelineIter);
  auto socket = std::dynamic_pointer_cast<folly::AsyncSocket>(
      routingPipeline->getTransport());
  routingPipeline->transportInactive();
  socket->detachEventBase();
  uint64_t hash = std::hash<R>()(routingData.routingData);
  auto acceptor = acceptors_[hash % acceptors_.size()];
  acceptor->getEventBase()->runInEventBaseThread(
      [ =, routingData = std::move(routingData) ]() mutable {
        socket->attachEventBase(acceptor->getEventBase());
        auto routingHandler =
            routingPipeline->template getHandler<RoutingDataHandler<R>>();
        DCHECK(routingHandler);
        auto transportInfo = routingPipeline->getTransportInfo();
        auto pipeline = childPipelineFactory_->newPipeline(
            socket, routingData.routingData, routingHandler, transportInfo);
        auto connection =
            new typename ServerAcceptor<Pipeline>::ServerConnection(pipeline);
        acceptor->addConnection(connection);
        pipeline->transportActive();
        pipeline->read(routingData.bufQueue);
      });
}

