 struct timespec ns_to_timespec(const s64 nsec)
 {
 	struct timespec ts;
	s32 rem;
 	if (!nsec)
 		return (struct timespec) {0, 0};
	ts.tv_sec = div_s64_rem(nsec, NSEC_PER_SEC, &rem);
	if (unlikely(rem < 0)) {
		ts.tv_sec--;
		rem += NSEC_PER_SEC;
	}
	ts.tv_nsec = rem;
 	return ts;
 }

void Compute(OpKernelContext* ctx) override {
  const Tensor& sorted_inputs_t = ctx->input(0);
  const Tensor& values_t = ctx->input(1);
  OP_REQUIRES(
      ctx, sorted_inputs_t.shape().dims() >= 2,
      errors::InvalidArgument("sorted input argument must be a matrix"));
  OP_REQUIRES(ctx, sorted_inputs_t.dim_size(0) == values_t.dim_size(0),
              Status(error::INVALID_ARGUMENT,
                     "Leading dim_size of both tensors must match."));
  OP_REQUIRES(ctx, values_t.NumElements() < std::numeric_limits<int>::max(),
              Status(error::INVALID_ARGUMENT,
                     "values tensor size must less than INT_MAX"));
  Tensor* output_t;
  OP_REQUIRES_OK(ctx, ctx->allocate_output(0, values_t.shape(), &output_t));
  if (output_t->dtype() == DT_INT32) {
    OP_REQUIRES(ctx,
                FastBoundsCheck(sorted_inputs_t.dim_size(1),
                                std::numeric_limits<int>::max()),
                errors::InvalidArgument("trailing dim_size must less than "
                                        "INT_MAX for int32 output type, was ",
                                        sorted_inputs_t.dim_size(1)));
  }
  auto output = output_t->template flat<OutType>();
  const auto sorted_inputs = sorted_inputs_t.template flat<T>();
  const auto values = values_t.template flat<T>();
  if (sorted_inputs.size() == 0) {
    functor::SetZeroFunctor<Device, OutType> set_zero;
    set_zero(ctx->eigen_device<Device>(), output);
    return;
  }
  OP_REQUIRES_OK(
      ctx, functor::LowerBoundFunctor<Device, T, OutType>::Compute(
               ctx, sorted_inputs, values, sorted_inputs_t.dim_size(0),
               sorted_inputs_t.dim_size(1), values_t.dim_size(1), &output));
}

void Server::CheckDocRoot() {
  try {
    if (!fs::exists(doc_root_) || !fs::is_directory(doc_root_)) {
      LOG_ERRO("Doc root is not an existing directory!");
      return;
    }
    if (doc_root_.is_relative()) {
      doc_root_ = fs::absolute(doc_root_);
    }
    doc_root_ = fs::canonical(doc_root_);
  } catch (fs::filesystem_error& e) {
    LOG_ERRO("Doc root error: %s", e.what());
    doc_root_.clear();
  }
  LOG_INFO("Doc root: %s", doc_root_.u8string().c_str());
}

static ssize_t compress(zckCtx *zck, zckComp *comp, const char *src,
                        const size_t src_size, char **dst, size_t *dst_size,
                        bool use_dict) {
    VALIDATE_INT(zck);
    ALLOCD_INT(zck, dst);
    ALLOCD_INT(zck, src);
    ALLOCD_INT(zck, dst_size);
    ALLOCD_INT(zck, comp);
    if((comp->dc_data_size > comp->dc_data_size + src_size) ||
       (src_size > comp->dc_data_size + src_size)) {
        zck_log(ZCK_LOG_ERROR, "Integer overflow when reading decompressed data");
        return false;
    }
    comp->dc_data = zrealloc(comp->dc_data, comp->dc_data_size + src_size);
    if (!comp->dc_data) {
        zck_log(ZCK_LOG_ERROR, "OOM in %s", __func__);
        return -1;
    }
    memcpy(comp->dc_data + comp->dc_data_size, src, src_size);
    *dst = NULL;
    *dst_size = 0;
    return 0;
}

