uint8_t ethereum_extractThorchainData(const EthereumSignTx *msg,
                                          char *buffer) {
  uint16_t offset = 4 + (5 * 32);
  int16_t len = msg->data_length - offset;
  if (msg->has_data_length && len > 0) {
    memcpy(buffer, msg->data_initial_chunk.bytes + offset, len);
    return len < 256 ? (uint8_t)len : 0;
  }
  return 0;
}

CmdResult Handle (const std::vector<std::string>& parameters, User *user)
{
	if (user->registered != REG_ALL)
	{
		if (!cap.ext.get(user))
			return CMD_FAILURE;
		if (parameters[0].find(' ') != std::string::npos || parameters[0][0] == ':')
			return CMD_FAILURE;
		SaslAuthenticator *sasl = authExt.get(user);
		if (!sasl)
			authExt.set(user, new SaslAuthenticator(user, parameters[0]));
		else if (sasl->SendClientMessage(parameters) == false)	
		{
			sasl->AnnounceState();
			authExt.unset(user);
		}
	}
	return CMD_FAILURE;
}

