void LinearAlgebraOp<InputScalar, OutputScalar>::PrepareOutputs(
    OpKernelContext* context, const TensorShapes& input_matrix_shapes,
    const TensorShape& batch_shape, TensorOutputs* outputs,
    TensorShapes* output_matrix_shapes) {
  *output_matrix_shapes = GetOutputMatrixShapes(input_matrix_shapes);
  const int num_outputs = output_matrix_shapes->size();
  OP_REQUIRES(
      context, num_outputs <= context->num_outputs(),
      errors::Internal(
          "Derived class expected more outputs (%d) that the op has (%d).",
          num_outputs, context->num_outputs()));
  std::set<int> unused_inputs;
  for (int input_idx = 0; input_idx < context->num_inputs(); ++input_idx) {
    unused_inputs.insert(input_idx);
  }
  for (int output_idx = 0; output_idx < context->num_outputs(); ++output_idx) {
    TensorShape output_tensor_shape({});
    if (output_idx < num_outputs) {
      const TensorShape& output_matrix_shape =
          output_matrix_shapes->at(output_idx);
      OP_REQUIRES(context, output_matrix_shape.dims() <= 2,
                  errors::InvalidArgument(
                      "Rank of matrix output no. %d must be 0, 1 or 2, got %d.",
                      output_idx, output_matrix_shape.dims()));
      output_tensor_shape = batch_shape;
      output_tensor_shape.AppendShape(output_matrix_shape);
    }
    Tensor* out = nullptr;
    bool reused_input = false;
    if (EnableInputForwarding()) {
      for (int input_idx : unused_inputs) {
        if (context->forward_input_to_output_with_shape(
                input_idx, output_idx, output_tensor_shape, &out)) {
          reused_input = true;
          unused_inputs.erase(input_idx);
          break;
        }
      }
    }
    if (!reused_input) {
      OP_REQUIRES_OK(context, context->allocate_output(
                                  output_idx, output_tensor_shape, &out));
    }
    OP_REQUIRES(
        context, out->dtype() == DataTypeToEnum<OutputScalar>::v(),
        errors::InvalidArgument("Invalid output dtype ", out->dtype(), " vs ",
                                DataTypeToEnum<OutputScalar>::v()));
    outputs->emplace_back(out);
  }
}

Status BuildInputArgIndex(const OpDef::ArgDef& arg_def, AttrSlice attr_values,
                          const FunctionDef::ArgAttrs* arg_attrs,
                          bool ints_on_device,
                          int64_t resource_arg_unique_id) {
  bool is_type_list;
  DataTypeVector dtypes;
  TF_RETURN_IF_ERROR(
      ArgNumType(attr_values, arg_def, &is_type_list, &dtypes));
  CHECK_GE(dtypes.size(), size_t{1});
  int arg_index = result_.nodes.size();
  TF_RETURN_IF_ERROR(
      AddItem(arg_def.name(), {true, arg_index, 0, is_type_list, dtypes}));
  for (size_t i = 0; i < dtypes.size(); ++i) {
    TF_RETURN_IF_ERROR(AddItem(strings::StrCat(arg_def.name(), ":", i),
                               {true, arg_index, 0, false, {dtypes[i]}}));
    DCHECK_EQ(arg_index, result_.nodes.size());
    string name = arg_def.name();
    if (dtypes.size() > 1) {
      strings::StrAppend(&name, "_", i);
    }
    NodeDef* gnode = AddNode(name);
    if (ints_on_device && dtypes[i] == DataType::DT_INT32) {
      gnode->set_op(FunctionLibraryDefinition::kDeviceArgOp);
    } else {
      gnode->set_op(FunctionLibraryDefinition::kArgOp);
    }
    DataType dtype = arg_def.is_ref() ? MakeRefType(dtypes[i]) : dtypes[i];
    AddAttr("T", dtype, gnode);
    AddAttr("index", arg_index, gnode);
    if (resource_arg_unique_id >= 0) {
      AddAttr("_resource_arg_unique_id", resource_arg_unique_id, gnode);
    }
    if (arg_attrs) {
      for (const auto& arg_attr : arg_attrs->attr()) {
        AddAttr(arg_attr.first, arg_attr.second, gnode->mutable_attr());
      }
    }
    result_.arg_types.push_back(dtypes[i]);
    ++arg_index;
  }
  return Status::OK();
}

static OPJ_BOOL opj_j2k_write_sod(opj_j2k_t *p_j2k,
                                  opj_tcd_t * p_tile_coder,
                                  OPJ_BYTE * p_data,
                                  OPJ_UINT32 * p_data_written,
                                  OPJ_UINT32 p_total_data_size,
                                  const opj_stream_private_t *p_stream,
                                  opj_event_mgr_t * p_manager
                                 )
{
    opj_codestream_info_t *l_cstr_info = 00;
    OPJ_UINT32 l_remaining_data;
    assert(p_j2k != 00);
    assert(p_manager != 00);
    assert(p_stream != 00);
    OPJ_UNUSED(p_stream);
    if (p_total_data_size < 4) {
        opj_event_msg(p_manager, EVT_ERROR,
                      "Not enough bytes in output buffer to write SOD marker\n");
        return OPJ_FALSE;
    }
    opj_write_bytes(p_data, J2K_MS_SOD,
                    2);                                 
    p_data += 2;
    l_remaining_data =  p_total_data_size - 4;
    p_tile_coder->tp_num =
        p_j2k->m_specific_param.m_encoder.m_current_poc_tile_part_number ;
    p_tile_coder->cur_tp_num =
        p_j2k->m_specific_param.m_encoder.m_current_tile_part_number;
#ifdef USE_JPWL
    assert(0 && "TODO");
#endif 
    if (p_j2k->m_specific_param.m_encoder.m_current_tile_part_number == 0) {
        p_tile_coder->tcd_image->tiles->packno = 0;
        if (l_cstr_info) {
            l_cstr_info->packno = 0;
        }
    }
    *p_data_written = 0;
    if (! opj_tcd_encode_tile(p_tile_coder, p_j2k->m_current_tile_number, p_data,
                              p_data_written, l_remaining_data, l_cstr_info,
                              p_manager)) {
        opj_event_msg(p_manager, EVT_ERROR, "Cannot encode tile\n");
        return OPJ_FALSE;
    }
    *p_data_written += 2;
    return OPJ_TRUE;
}

