	unsigned long seqid;
	GET_SEQ_FROM_MSGID(msgid, seq, seqid);
	spin_lock_irqsave(&intf->seq_lock, flags);
	if ((intf->seq_table[seq].inuse)
				&& (intf->seq_table[seq].seqid == seqid)) {
