int mongo_env_write_socket( mongo *conn, const void *buf, int len ) {
    const char *cbuf = buf;
    int flags = 0;
    while ( len ) {
        int sent = send( conn->sock, cbuf, len, flags );
        if ( sent == -1 ) {
            __mongo_set_error( conn, MONGO_IO_ERROR, NULL, WSAGetLastError() );
            conn->connected = 0;
            return MONGO_ERROR;
        }
        cbuf += sent;
        len -= sent;
    }
    return MONGO_OK;
}

static int sched_read_attr(struct sched_attr __user *uattr,
			   struct sched_attr *attr,
			   unsigned int usize)
{
	int ret;
	if (!access_ok(VERIFY_WRITE, uattr, usize))
		return -EFAULT;
	if (usize < sizeof(*attr)) {
		unsigned char *addr;
		unsigned char *end;
		addr = (void *)attr + usize;
		end  = (void *)attr + sizeof(*attr);
		for (; addr < end; addr++) {
			if (*addr)
				goto err_size;
		}
		attr->size = usize;
	}
	ret = copy_to_user(uattr, attr, usize);
	if (ret)
		return -EFAULT;
out:
	return ret;
err_size:
	ret = -E2BIG;
	goto out;
}

matchDomainName(const char *h, const char *d, MatchDomainNameFlags flags)
{
    int dl;
    int hl;
    const bool hostIncludesSubdomains = (*h == '.');
    while ('.' == *h)
        ++h;
    hl = strlen(h);
    if (hl == 0)
        return -1;
    dl = strlen(d);
    if (dl == 0)
        return 1;
    while (xtolower(h[--hl]) == xtolower(d[--dl])) {
        if (hl == 0 && dl == 0) {
            return 0;
        }
        if (0 == hl) {
            if (1 == dl && '.' == d[0])
                return 0;
            else
                return -1;
        }
        if (0 == dl) {
            if ('.' == d[0]) {
                if (flags & mdnRejectSubsubDomains) {
                    while(--hl >= 0 && h[hl] != '.');
                    if (hl < 0) {
                        return hostIncludesSubdomains ? 1 : 0;
                    } else
                        return 1; 
                } else
                    return 0;
            } else
                return 1;
        }
    }
    if ((flags & mdnHonorWildcards) && h[hl] == '*' && h[hl + 1] == '.')
        return 0;
    if ('.' == d[dl])
        return 1;
    if ('.' == h[hl])
        return -1;
    return (xtolower(h[hl]) - xtolower(d[dl]));
}

void PdfXRefStreamParserObject::getIndices(vector<int64_t>& indices, int64_t size)
{
    auto indexObj = this->GetDictionary().GetKey("Index");
    if (indexObj == nullptr)
    {
        indices.push_back(static_cast<int64_t>(0));
        indices.push_back(size);
    }
    else
    {
        const PdfArray* arr;
        if (!indexObj->TryGetArray(arr))
            PODOFO_RAISE_ERROR_INFO(PdfErrorCode::NoXRef, "Invalid XRef Stream /Index");
        for (auto index : *arr)
            indices.push_back(index.GetNumber());
    }
    if (indices.size() % 2 != 0)
        PODOFO_RAISE_ERROR_INFO(PdfErrorCode::NoXRef, "Invalid XRef Stream /Index");
}

passGetEmphasis ()
{
  int more = 1;
  passLinepos++;
  passEmphasis = 0;
  while (more)
    {
      switch (passLine.chars[passLinepos])
	{
	case 'i':
	  passEmphasis |= italic;
	  break;
	case 'b':
	  passEmphasis |= bold;
	  break;
	case 'u':
	  passEmphasis |= underline;
	  break;
	case 'c':
	  passEmphasis |= computer_braille;
	  break;
	default:
	  more = 0;
	  break;
	}
      if (more)
	passLinepos++;
    }
  if (!passEmphasis)
    {
      compileError (passNested, "emphasis indicators expected");
      passLinepos--;
      return 0;
    }
  return 1;
}

