static u64 distribute_cfs_runtime(struct cfs_bandwidth *cfs_b,
		u64 remaining, u64 expires)
{
	struct cfs_rq *cfs_rq;
	u64 runtime;
	u64 starting_runtime = remaining;
	rcu_read_lock();
	list_for_each_entry_rcu(cfs_rq, &cfs_b->throttled_cfs_rq,
				throttled_list) {
		struct rq *rq = rq_of(cfs_rq);
		struct rq_flags rf;
		rq_lock_irqsave(rq, &rf);
		if (!cfs_rq_throttled(cfs_rq))
			goto next;
		runtime = -cfs_rq->runtime_remaining + 1;
		if (runtime > remaining)
			runtime = remaining;
		remaining -= runtime;
		cfs_rq->runtime_remaining += runtime;
		cfs_rq->runtime_expires = expires;
		if (cfs_rq->runtime_remaining > 0)
			unthrottle_cfs_rq(cfs_rq);
next:
		rq_unlock_irqrestore(rq, &rf);
		if (!remaining)
			break;
	}
	rcu_read_unlock();
	return starting_runtime - remaining;
}

static int stimer_set_count(struct kvm_vcpu_hv_stimer *stimer, u64 count,
			    bool host)
{
	struct kvm_vcpu *vcpu = hv_stimer_to_vcpu(stimer);
	struct kvm_vcpu_hv_synic *synic = to_hv_synic(vcpu);
	if (!synic->active && !host)
		return 1;
	trace_kvm_hv_stimer_set_count(hv_stimer_to_vcpu(stimer)->vcpu_id,
				      stimer->index, count, host);
	stimer_cleanup(stimer);
	stimer->count = count;
	if (stimer->count == 0)
		stimer->config.enable = 0;
	else if (stimer->config.auto_enable)
		stimer->config.enable = 1;
	if (stimer->config.enable)
		stimer_mark_pending(stimer, false);
	return 0;
}

_SSL_match_hostname (const char *cert_hostname, const char *hostname)
{
	const char *cert_domain, *domain, *next_dot;
	if (g_ascii_strcasecmp (cert_hostname, hostname) == 0)
		return 0;
	if (cert_hostname[0] == '*')
	{
		cert_domain = &cert_hostname[1];
		if (cert_domain[0] == '\0')
			return -1;
		if (cert_domain[0] != '.')
			return -1;
		if (cert_domain[1] == '.')
			return -1;
		next_dot = strchr (&cert_domain[1], '.');
		if (next_dot == NULL)
			return -1;
		if (next_dot[1] == '.')
			return -1;
		domain = strchr (hostname, '.');
		if (domain == NULL || strlen(domain) == 1)
			return -1;
		if (g_ascii_strcasecmp (cert_domain, domain) == 0)
			return 0;
	}
	return -1;
}

static int __init mbochs_dev_init(void)
{
	int ret = 0;
	atomic_set(&mbochs_avail_mbytes, max_mbytes);
	ret = alloc_chrdev_region(&mbochs_devt, 0, MINORMASK + 1, MBOCHS_NAME);
	if (ret < 0) {
		pr_err("Error: failed to register mbochs_dev, err: %d\n", ret);
		return ret;
	}
	cdev_init(&mbochs_cdev, &vd_fops);
	cdev_add(&mbochs_cdev, mbochs_devt, MINORMASK + 1);
	pr_info("%s: major %d\n", __func__, MAJOR(mbochs_devt));
	ret = mdev_register_driver(&mbochs_driver);
	if (ret)
		goto err_cdev;
	mbochs_class = class_create(THIS_MODULE, MBOCHS_CLASS_NAME);
	if (IS_ERR(mbochs_class)) {
		pr_err("Error: failed to register mbochs_dev class\n");
		ret = PTR_ERR(mbochs_class);
		goto err_driver;
	}
	mbochs_dev.class = mbochs_class;
	mbochs_dev.release = mbochs_device_release;
	dev_set_name(&mbochs_dev, "%s", MBOCHS_NAME);
	ret = device_register(&mbochs_dev);
	if (ret)
		goto err_class;
	ret = mdev_register_device(&mbochs_dev, &mdev_fops);
	if (ret)
		goto err_device;
	return 0;
err_device:
	device_unregister(&mbochs_dev);
err_class:
	class_destroy(mbochs_class);
err_driver:
	mdev_unregister_driver(&mbochs_driver);
err_cdev:
	cdev_del(&mbochs_cdev);
	unregister_chrdev_region(mbochs_devt, MINORMASK + 1);
	return ret;
}

int kvm_arch_vcpu_ioctl_run(struct kvm_vcpu *vcpu, struct kvm_run *run)
{
	int ret;
	sigset_t sigsaved;
	if (unlikely(vcpu->arch.target < 0))
		return -ENOEXEC;
	ret = kvm_vcpu_first_run_init(vcpu);
	if (ret)
		return ret;
	if (run->exit_reason == KVM_EXIT_MMIO) {
		ret = kvm_handle_mmio_return(vcpu, vcpu->run);
		if (ret)
			return ret;
	}
	if (vcpu->sigset_active)
		sigprocmask(SIG_SETMASK, &vcpu->sigset, &sigsaved);
	ret = 1;
	run->exit_reason = KVM_EXIT_UNKNOWN;
	while (ret > 0) {
		cond_resched();
		update_vttbr(vcpu->kvm);
		if (vcpu->arch.pause)
			vcpu_pause(vcpu);
		kvm_vgic_flush_hwstate(vcpu);
		kvm_timer_flush_hwstate(vcpu);
		local_irq_disable();
		if (signal_pending(current)) {
			ret = -EINTR;
			run->exit_reason = KVM_EXIT_INTR;
		}
		if (ret <= 0 || need_new_vmid_gen(vcpu->kvm)) {
			local_irq_enable();
			kvm_timer_sync_hwstate(vcpu);
			kvm_vgic_sync_hwstate(vcpu);
			continue;
		}
		trace_kvm_entry(*vcpu_pc(vcpu));
		kvm_guest_enter();
		vcpu->mode = IN_GUEST_MODE;
		ret = kvm_call_hyp(__kvm_vcpu_run, vcpu);
		vcpu->mode = OUTSIDE_GUEST_MODE;
		vcpu->arch.last_pcpu = smp_processor_id();
		kvm_guest_exit();
		trace_kvm_exit(*vcpu_pc(vcpu));
		local_irq_enable();
		kvm_timer_sync_hwstate(vcpu);
		kvm_vgic_sync_hwstate(vcpu);
		ret = handle_exit(vcpu, run, ret);
	}
	if (vcpu->sigset_active)
		sigprocmask(SIG_SETMASK, &sigsaved, NULL);
	return ret;
}

