friend H AbslHashValue(H h, const TensorKey& k) {
  const uint8* d = static_cast<uint8*>(k.data());
  size_t s = k.AllocatedBytes();
  std::vector<uint8> vec;
  vec.reserve(s);
  for (int i = 0; i < s; i++) {
    vec.push_back(d[i]);
  }
  return H::combine(std::move(h), s);
}

static void do_free_publickey(struct rsa_public_key *key)
{
	crypto_bignum_free(key->e);
	crypto_bignum_free(key->n);
}

FIO_FUNC inline void fio_throttle_thread(size_t nano_sec) {
