static inline int quic_token_validate(struct quic_rx_packet *pkt,
                                      struct quic_dgram *dgram,
                                      struct listener *l, struct quic_conn *qc,
                                      struct quic_cid *odcid)
{
	int ret = 0;
	TRACE_ENTER(QUIC_EV_CONN_LPKT, qc);
	switch (*pkt->token) {
	case QUIC_TOKEN_FMT_RETRY:
		ret = quic_retry_token_check(pkt, dgram, l, qc, odcid);
		break;
	case QUIC_TOKEN_FMT_NEW:
		ret = quic_token_check(pkt, dgram, qc);
		if (!ret) {
			dgram->flags |= QUIC_DGRAM_FL_SEND_RETRY;
		}
		break;
	default:
		TRACE_PROTO("Packet dropped", QUIC_EV_CONN_LPKT, qc, NULL, NULL, pkt->version);
		break;
	}
	if (!ret)
		goto err;
	ret = 1;
 leave:
	TRACE_LEAVE(QUIC_EV_CONN_LPKT, qc);
	return ret;
 err:
	TRACE_DEVEL("leaving in error", QUIC_EV_CONN_LPKT, qc);
	goto leave;
}

gss_get_mic_iov_length(OM_uint32 *minor_status, gss_ctx_id_t context_handle,
		       gss_qop_t qop_req, gss_iov_buffer_desc *iov,
		       int iov_count)
{
    OM_uint32 status;
    gss_union_ctx_id_t ctx;
    gss_mechanism mech;
    status = val_wrap_iov_args(minor_status, context_handle, 0, qop_req, NULL,
			       iov, iov_count);
    if (status != GSS_S_COMPLETE)
	return status;
    ctx = (gss_union_ctx_id_t)context_handle;
    if (ctx->internal_ctx_id == GSS_C_NO_CONTEXT)
	return GSS_S_NO_CONTEXT;
    mech = gssint_get_mechanism(ctx->mech_type);
    if (mech == NULL)
	return GSS_S_BAD_MECH;
    if (mech->gss_get_mic_iov_length == NULL)
	return GSS_S_UNAVAILABLE;
    status = mech->gss_get_mic_iov_length(minor_status, ctx->internal_ctx_id,
					  qop_req, iov, iov_count);
    if (status != GSS_S_COMPLETE)
	map_error(minor_status, mech);
    return status;
}

xps_parse_digits(char *s, int *digit)
{
    *digit = 0;
    while (*s >= '0' && *s <= '9')
