void ComputeAsync(OpKernelContext* c, DoneCallback done) override {
  auto col_params = new CollectiveParams();
  auto done_with_cleanup = [col_params, done = std::move(done)]() {
    done();
    col_params->Unref();
  };
  core::RefCountPtr<CollectiveGroupResource> resource;
  OP_REQUIRES_OK_ASYNC(c, LookupResource(c, HandleFromInput(c, 1), &resource),
                       done);
  Tensor group_assignment = c->input(2);
  OP_REQUIRES_OK_ASYNC(
      c,
      FillCollectiveParams(col_params, group_assignment, REDUCTION_COLLECTIVE,
                           resource.get()),
      done);
  col_params->instance.shape = c->input(0).shape();
  col_params->merge_op = merge_op_.get();
  col_params->final_op = final_op_.get();
  VLOG(1) << "CollectiveReduceV3 group_size " << col_params->group.group_size
          << " group_key " << col_params->group.group_key << " instance_key "
          << col_params->instance.instance_key;
  Tensor* output = nullptr;
  OP_REQUIRES_OK_ASYNC(c,
                       c->forward_input_or_allocate_output(
                           {0}, 0, col_params->instance.shape, &output),
                       done_with_cleanup);
  Run(c, col_params, std::move(done_with_cleanup));
}

int64_t OpLevelCostEstimator::CalculateOutputSize(const OpInfo& op_info,
                                                  bool* found_unknown_shapes) {
  int64_t total_output_size = 0;
  for (const auto& output : op_info.outputs()) {
    DataType dt = output.dtype();
    const auto& original_output_shape = output.shape();
    int64_t output_size = DataTypeSize(BaseType(dt));
    int num_dims = std::max(1, original_output_shape.dim_size());
    auto output_shape = MaybeGetMinimumShape(original_output_shape, num_dims,
                                             found_unknown_shapes);
    for (const auto& dim : output_shape.dim()) {
      output_size *= dim.size();
    }
    total_output_size += output_size;
    VLOG(1) << "Output Size: " << output_size
            << " Total Output Size:" << total_output_size;
  }
  return total_output_size;
}

static int parse_token(char **name, char **value, char **cp)
{
	char *end;
	if (!name || !value || !cp)
		return -BLKID_ERR_PARAM;
	if (!(*value = strchr(*cp, '=')))
		return 0;
	**value = '\0';
	*name = strip_line(*cp);
	*value = skip_over_blank(*value + 1);
	if (**value == '"') {
		end = strchr(*value + 1, '"');
		if (!end) {
			DBG(READ, ul_debug("unbalanced quotes at: %s", *value));
			*cp = *value;
			return -BLKID_ERR_CACHE;
		}
		(*value)++;
		*end = '\0';
		end++;
	} else {
		end = skip_over_word(*value);
		if (*end) {
			*end = '\0';
			end++;
		}
	}
	*cp = end;
	return 1;
}

static void prefetch_table(const volatile byte *tab, size_t len)
{
  size_t i;
  for (i = 0; i < len; i += 8 * 32)
    {
      (void)tab[i + 0 * 32];
      (void)tab[i + 1 * 32];
      (void)tab[i + 2 * 32];
      (void)tab[i + 3 * 32];
      (void)tab[i + 4 * 32];
      (void)tab[i + 5 * 32];
      (void)tab[i + 6 * 32];
      (void)tab[i + 7 * 32];
    }
  (void)tab[len - 1];
}

find_auth_end (FlatpakProxyClient *client, Buffer *buffer)
{
  guchar *match;
  int i;
  if (client->auth_end_offset > 0)
    {
      gsize left = strlen (AUTH_END_STRING) - client->auth_end_offset;
      gsize to_match = MIN (left, buffer->pos);
      if (memcmp (buffer->data, &AUTH_END_STRING[client->auth_end_offset], to_match) == 0)
        {
          client->auth_end_offset += to_match;
          if (client->auth_end_offset == strlen (AUTH_END_STRING))
            return to_match;
          return -1;
        }
      client->auth_end_offset = -1;
    }
  match = memmem (buffer, buffer->pos,
                  AUTH_END_STRING, strlen (AUTH_END_STRING));
  if (match != NULL)
    return match - buffer->data + strlen (AUTH_END_STRING);
  for (i = MIN (strlen (AUTH_END_STRING) - 1, buffer->pos); i > 0; i--)
    {
      if (memcmp (buffer->data + buffer->pos - i, AUTH_END_STRING, i) == 0)
        {
          client->auth_end_offset = i;
          break;
        }
    }
  return -1;
}

