main (int argc, char **argv)
{
	EvArchive *ar;
	EvArchiveType ar_type;
	GError *error = NULL;
	gboolean printed_header = FALSE;
	if (argc != 3) {
		usage (argv[0]);
		return 1;
	}
	ar_type = str_to_archive_type (argv[1]);
	if (ar_type == EV_ARCHIVE_TYPE_NONE)
		return 1;
	ar = ev_archive_new ();
	if (!ev_archive_set_archive_type (ar, ar_type)) {
		g_warning ("Failed to set archive type");
		goto out;
	}
	if (!ev_archive_open_filename (ar, argv[2], &error)) {
		g_warning ("Failed to open '%s': %s",
			   argv[2], error->message);
		g_error_free (error);
		goto out;
	}
	while (1) {
		const char *name;
		gboolean is_encrypted;
		gint64 size;
		if (!ev_archive_read_next_header (ar, &error)) {
			if (error != NULL) {
				g_warning ("Fatal error handling archive: %s", error->message);
				g_clear_error (&error);
				goto out;
			}
			break;
		}
		name = ev_archive_get_entry_pathname (ar);
		is_encrypted = ev_archive_get_entry_is_encrypted (ar);
		size = ev_archive_get_entry_size (ar);
		if (!printed_header) {
			g_print ("P\tSIZE\tNAME\n");
			printed_header = TRUE;
		}
		g_print ("%c\t%"G_GINT64_FORMAT"\t%s\n",
			 is_encrypted ? 'P' : ' ',
			 size, name);
	}
	ev_archive_reset (ar);
	g_clear_object (&ar);
	return 0;
out:
	g_clear_object (&ar);
	return 1;
}

static GF_Err BM_ParseGlobalQuantizer(GF_BifsDecoder *codec, GF_BitStream *bs, GF_List *com_list)
{
	GF_Node *node;
	GF_Command *com;
	GF_CommandField *inf;
	node = gf_bifs_dec_node(codec, bs, NDT_SFWorldNode);
	if (!node) return GF_NON_COMPLIANT_BITSTREAM;
	if (codec->scenegraph->global_qp) {
		gf_node_unregister(codec->scenegraph->global_qp, NULL);
	}
	codec->ActiveQP = NULL;
	codec->scenegraph->global_qp = NULL;
	if (gf_node_get_tag(node) != TAG_MPEG4_QuantizationParameter) {
		gf_node_unregister(node, NULL);
		return GF_NON_COMPLIANT_BITSTREAM;
	}
	codec->ActiveQP = (M_QuantizationParameter *) node;
	codec->ActiveQP->isLocal = 0;
	codec->scenegraph->global_qp = node;
	node->sgprivate->num_instances = 2;
	com = gf_sg_command_new(codec->current_graph, GF_SG_GLOBAL_QUANTIZER);
	inf = gf_sg_command_field_new(com);
	inf->new_node = node;
	inf->field_ptr = &inf->new_node;
	inf->fieldType = GF_SG_VRML_SFNODE;
	gf_list_add(com_list, com);
	return GF_OK;
}

explicit FractionalMaxPoolOp(OpKernelConstruction* context)
    : OpKernel(context) {
  OP_REQUIRES_OK(context, context->GetAttr("pooling_ratio", &pooling_ratio_));
  OP_REQUIRES_OK(context, context->GetAttr("pseudo_random", &pseudo_random_));
  OP_REQUIRES_OK(context, context->GetAttr("overlapping", &overlapping_));
  OP_REQUIRES(context, pooling_ratio_.size() == 4,
              errors::InvalidArgument("pooling_ratio field must "
                                      "specify 4 dimensions"));
  for (std::size_t i = 0; i < pooling_ratio_.size(); ++i) {
    OP_REQUIRES(context, pooling_ratio_[i] >= 1,
                errors::InvalidArgument(
                    "pooling_ratio cannot be smaller than 1, got: ",
                    pooling_ratio_[i]));
  }
  OP_REQUIRES(
      context, pooling_ratio_[0] == 1 && pooling_ratio_[3] == 1,
      errors::Unimplemented("Fractional max pooling is not yet "
                            "supported on the batch nor channel dimension."));
  OP_REQUIRES_OK(context, context->GetAttr("deterministic", &deterministic_));
  OP_REQUIRES_OK(context, context->GetAttr("seed", &seed_));
  OP_REQUIRES_OK(context, context->GetAttr("seed2", &seed2_));
  if (deterministic_) {
    if ((seed_ == 0) && (seed2_ == 0)) {
      seed_ = random::New64();
      seed2_ = random::New64();
    }
  } else {
    OP_REQUIRES(
        context, (seed_ == 0) && (seed2_ == 0),
        errors::InvalidArgument(
            "Both seed and seed2 should be 0 if deterministic is false."));
  }
}

