}

static __net_init int setup_net(struct net *net, struct user_namespace *user_ns)
{
	const struct pernet_operations *ops, *saved_ops;
	int error = 0;
	LIST_HEAD(net_exit_list);
	refcount_set(&net->count, 1);
	refcount_set(&net->passive, 1);
	get_random_bytes(&net->hash_mix, sizeof(u32));
	net->dev_base_seq = 1;
	net->user_ns = user_ns;
	idr_init(&net->netns_ids);
	spin_lock_init(&net->nsid_lock);
	mutex_init(&net->ipv4.ra_mutex);
	list_for_each_entry(ops, &pernet_list, list) {
		error = ops_init(ops, net);
		if (error < 0)
			goto out_undo;
	}
	down_write(&net_rwsem);
	list_add_tail_rcu(&net->list, &net_namespace_list);
	up_write(&net_rwsem);
out:
	return error;
out_undo:
	list_add(&net->exit_list, &net_exit_list);
	saved_ops = ops;
	list_for_each_entry_continue_reverse(ops, &pernet_list, list)
		ops_exit_list(ops, &net_exit_list);
	ops = saved_ops;
	list_for_each_entry_continue_reverse(ops, &pernet_list, list)
		ops_free_list(ops, &net_exit_list);
	rcu_barrier();
	goto out;
}

static void youngcollection (lua_State *L, global_State *g) {
  GCObject **psurvival;  
  lua_assert(g->gcstate == GCSpropagate);
  markold(g, g->survival, g->reallyold);
  markold(g, g->finobj, g->finobjrold);
  atomic(L);
  psurvival = sweepgen(L, g, &g->allgc, g->survival);
  sweepgen(L, g, psurvival, g->reallyold);
  g->reallyold = g->old;
  g->old = *psurvival;  
  g->survival = g->allgc;  
  psurvival = sweepgen(L, g, &g->finobj, g->finobjsur);
  sweepgen(L, g, psurvival, g->finobjrold);
  g->finobjrold = g->finobjold;
  g->finobjold = *psurvival;  
  g->finobjsur = g->finobj;  
  sweepgen(L, g, &g->tobefnz, NULL);
  finishgencycle(L, g);
}

int bridge__remap_topic_in(struct mosquitto *context, char **topic)
{
	struct mosquitto__bridge_topic *cur_topic;
	char *topic_temp;
	int i;
	size_t len;
	int rc;
	bool match;
	if(context->bridge && context->bridge->topics && context->bridge->topic_remapping){
		for(i=0; i<context->bridge->topic_count; i++){
			cur_topic = &context->bridge->topics[i];
			if((cur_topic->direction == bd_both || cur_topic->direction == bd_in)
					&& (cur_topic->remote_prefix || cur_topic->local_prefix)){
				rc = mosquitto_topic_matches_sub(cur_topic->remote_topic, *topic, &match);
				if(rc){
					mosquitto__free(*topic);
					*topic = NULL;
					return rc;
				}
				if(match){
					if(cur_topic->remote_prefix){
						if(!strncmp(cur_topic->remote_prefix, *topic, strlen(cur_topic->remote_prefix))){
							topic_temp = mosquitto__strdup((*topic)+strlen(cur_topic->remote_prefix));
							if(!topic_temp){
								mosquitto__free(*topic);
								*topic = NULL;
								return MOSQ_ERR_NOMEM;
							}
							mosquitto__free(*topic);
							*topic = topic_temp;
						}
					}
					if(cur_topic->local_prefix){
						len = strlen(*topic) + strlen(cur_topic->local_prefix)+1;
						topic_temp = mosquitto__malloc(len+1);
						if(!topic_temp){
							mosquitto__free(*topic);
							*topic = NULL;
							return MOSQ_ERR_NOMEM;
						}
						snprintf(topic_temp, len, "%s%s", cur_topic->local_prefix, *topic);
						topic_temp[len] = '\0';
						mosquitto__free(*topic);
						*topic = topic_temp;
					}
					break;
				}
			}
		}
	}
	return MOSQ_ERR_SUCCESS;
}

pf_remove_state(struct pf_state *st)
{
	PF_ASSERT_LOCKED();
	if (st->timeout == PFTM_UNLINKED)
		return;
	st->timeout = PFTM_UNLINKED;
	pf_postprocess_addr(st);
	if (st->src.state == PF_TCPS_PROXY_DST) {
		pf_send_tcp(st->rule.ptr, st->key[PF_SK_WIRE]->af,
		    &st->key[PF_SK_WIRE]->addr[1],
		    &st->key[PF_SK_WIRE]->addr[0],
		    st->key[PF_SK_WIRE]->port[1],
		    st->key[PF_SK_WIRE]->port[0],
		    st->src.seqhi, st->src.seqlo + 1,
		    TH_RST|TH_ACK, 0, 0, 0, 1, st->tag,
		    st->key[PF_SK_WIRE]->rdomain);
	}
	if (st->key[PF_SK_STACK]->proto == IPPROTO_TCP)
		pf_set_protostate(st, PF_PEER_BOTH, TCPS_CLOSED);
	RBT_REMOVE(pf_state_tree_id, &tree_id, st);
#if NPFLOW > 0
	if (st->state_flags & PFSTATE_PFLOW)
		export_pflow(st);
#endif	
#if NPFSYNC > 0
	pfsync_delete_state(st);
#endif	
	pf_src_tree_remove_state(st);
	pf_detach_state(st);
}

TfLiteStatus MockCustom::Invoke(TfLiteContext* context, TfLiteNode* node) {
  const TfLiteTensor* input = tflite::GetInput(context, node, 0);
  const int32_t* input_data = input->data.i32;
  const TfLiteTensor* weight = tflite::GetInput(context, node, 1);
  const uint8_t* weight_data = weight->data.uint8;
  TfLiteTensor* output = GetOutput(context, node, 0);
  int32_t* output_data = output->data.i32;
  output_data[0] =
      0;  
  output_data[0] = input_data[0] + weight_data[0];
  return kTfLiteOk;
}

