static void ip_expire(unsigned long arg)
{
	struct ipq *qp;
	struct net *net;
	qp = container_of((struct inet_frag_queue *) arg, struct ipq, q);
	net = container_of(qp->q.net, struct net, ipv4.frags);
	spin_lock(&qp->q.lock);
	if (qp->q.last_in & INET_FRAG_COMPLETE)
		goto out;
	ipq_kill(qp);
	IP_INC_STATS_BH(net, IPSTATS_MIB_REASMTIMEOUT);
	IP_INC_STATS_BH(net, IPSTATS_MIB_REASMFAILS);
	if ((qp->q.last_in & INET_FRAG_FIRST_IN) && qp->q.fragments != NULL) {
		struct sk_buff *head = qp->q.fragments;
		rcu_read_lock();
		head->dev = dev_get_by_index_rcu(net, qp->iif);
		if (!head->dev)
			goto out_rcu_unlock;
		if (qp->user == IP_DEFRAG_CONNTRACK_IN && !skb_dst(head)) {
			const struct iphdr *iph = ip_hdr(head);
			int err = ip_route_input(head, iph->daddr, iph->saddr,
						 iph->tos, head->dev);
			if (unlikely(err))
				goto out_rcu_unlock;
			if (skb_rtable(head)->rt_type != RTN_LOCAL)
				goto out_rcu_unlock;
		}
		icmp_send(head, ICMP_TIME_EXCEEDED, ICMP_EXC_FRAGTIME, 0);
out_rcu_unlock:
		rcu_read_unlock();
	}
out:
	spin_unlock(&qp->q.lock);
	ipq_put(qp);
}

static int ssl_parse_server_psk_hint( mbedtls_ssl_context *ssl,
                                      unsigned char **p,
                                      unsigned char *end )
{
    int ret = MBEDTLS_ERR_SSL_FEATURE_UNAVAILABLE;
    size_t  len;
    ((void) ssl);
    if( (*p) > end - 2 )
    {
        MBEDTLS_SSL_DEBUG_MSG( 1, ( "bad server key exchange message "
                                    "(psk_identity_hint length)" ) );
        return( MBEDTLS_ERR_SSL_BAD_HS_SERVER_KEY_EXCHANGE );
    }
    len = (*p)[0] << 8 | (*p)[1];
    *p += 2;
    if( (*p) + len > end )
    {
        MBEDTLS_SSL_DEBUG_MSG( 1, ( "bad server key exchange message "
                                    "(psk_identity_hint length)" ) );
        return( MBEDTLS_ERR_SSL_BAD_HS_SERVER_KEY_EXCHANGE );
    }
    *p += len;
    ret = 0;
    return( ret );
}

static pj_status_t get_name_len(int rec_counter, const pj_uint8_t *pkt, 
                                const pj_uint8_t *start, const pj_uint8_t *max, 
                                int *parsed_len, int *name_len)
{
    const pj_uint8_t *p;
    pj_status_t status;
    if (rec_counter > 10) {
        return PJLIB_UTIL_EDNSINNAMEPTR;
    }
    if (start >= max)
        return PJLIB_UTIL_EDNSINNAMEPTR;
    *name_len = *parsed_len = 0;
    p = start;
    while (*p) {
        if ((*p & 0xc0) == 0xc0) {
            int ptr_len = 0;
            int dummy;
            pj_uint16_t offset;
            pj_memcpy(&offset, p, 2);
            offset ^= pj_htons((pj_uint16_t)(0xc0 << 8));
            offset = pj_ntohs(offset);
            if (offset >= max - pkt)
                return PJLIB_UTIL_EDNSINNAMEPTR;
            status = get_name_len(rec_counter+1, pkt, pkt + offset, max, 
                                  &dummy, &ptr_len);
            if (status != PJ_SUCCESS)
                return status;
            *parsed_len += 2;
            *name_len += ptr_len;
            return PJ_SUCCESS;
        } else {
            unsigned label_len = *p;
            if (p+1+label_len+1 > max)
                return PJLIB_UTIL_EDNSINNAMEPTR;
            p += (label_len + 1);
            *parsed_len += (label_len + 1);
            if (*p != 0)
                ++label_len;
            *name_len += label_len;
        }
    }
    ++p;
    (*parsed_len)++;
    return PJ_SUCCESS;
}

static enum hrtimer_restart posix_timer_fn(struct hrtimer *timer)
{
	struct k_itimer *timr;
	unsigned long flags;
	int si_private = 0;
	enum hrtimer_restart ret = HRTIMER_NORESTART;
	timr = container_of(timer, struct k_itimer, it.real.timer);
	spin_lock_irqsave(&timr->it_lock, flags);
	timr->it_active = 0;
	if (timr->it_interval != 0)
		si_private = ++timr->it_requeue_pending;
	if (posix_timer_event(timr, si_private)) {
		if (timr->it_interval != 0) {
			ktime_t now = hrtimer_cb_get_time(timer);
#ifdef CONFIG_HIGH_RES_TIMERS
			{
				ktime_t kj = NSEC_PER_SEC / HZ;
				if (timr->it_interval < kj)
					now = ktime_add(now, kj);
			}
#endif
			timr->it_overrun += (unsigned int)
				hrtimer_forward(timer, now,
						timr->it_interval);
			ret = HRTIMER_RESTART;
			++timr->it_requeue_pending;
			timr->it_active = 1;
		}
	}
	unlock_timer(timr, flags);
	return ret;
}

