MYSOFA_EXPORT struct MYSOFA_HRTF* mysofa_load(const char *filename, int *err) {
	struct READER reader;
	struct MYSOFA_HRTF *hrtf = NULL;
	if (filename == NULL)
		filename = CMAKE_INSTALL_PREFIX "/share/libmysofa/default.sofa";
	if (strcmp(filename, "-"))
		reader.fhd = fopen(filename, "rb");
	else
		reader.fhd = stdin;
	if (!reader.fhd) {
		log("cannot open file %s\n", filename);
		*err = errno;
		return NULL;
	}
	reader.gcol = NULL;
	reader.all = NULL;
	reader.recursive_counter = 0;
	*err = superblockRead(&reader, &reader.superblock);
	if (!*err) {
		hrtf = getHrtf(&reader, err);
	}
	superblockFree(&reader, &reader.superblock);
	gcolFree(reader.gcol);
	if (strcmp(filename, "-"))
		fclose(reader.fhd);
	return hrtf;
}

void CoreUserInputHandler::handleMsg(const BufferInfo &bufferInfo, const QString &msg)
{
    Q_UNUSED(bufferInfo);
    if (!msg.contains(' '))
        return;
    QString target = msg.section(' ', 0, 0);
    QByteArray encMsg = userEncode(target, msg.section(' ', 1));
#ifdef HAVE_QCA2
    putPrivmsg(serverEncode(target), encMsg, network()->cipher(target));
#else
    putPrivmsg(serverEncode(target), encMsg);
#endif
}

flatpak_run_get_cups_server_name_config (const char *path)
{
  g_autoptr(GFile) file = g_file_new_for_path (path);
  g_autoptr(GError) my_error = NULL;
  g_autoptr(GFileInputStream) input_stream = NULL;
  g_autoptr(GDataInputStream) data_stream = NULL;
  size_t len;
  input_stream = g_file_read (file, NULL, &my_error);
  if (my_error)
    {
      g_debug ("CUPS configuration file '%s': %s", path, my_error->message);
      return NULL;
    }
  data_stream = g_data_input_stream_new (G_INPUT_STREAM (input_stream));
  while (TRUE)
    {
      g_autofree char *line = g_data_input_stream_read_line (data_stream, &len, NULL, NULL);
      if (line == NULL)
        break;
      g_strchug (line);
      if ((*line  == '\0') || (*line == '#'))
        continue;
      g_auto(GStrv) tokens = g_strsplit (line, " ", 2);
      if ((tokens[0] != NULL) && (tokens[1] != NULL))
        {
          if (strcmp ("ServerName", tokens[0]) == 0)
            {
              g_strchug (tokens[1]);
              if (flatpak_run_cups_check_server_is_socket (tokens[1]))
                return g_strdup (tokens[1]);
            }
        }
    }
    return NULL;
}

int32_t audio_io_osx::shutdown_audio_unit() {
    OSStatus result = -1;
    if (NULL != au_rec_) {
        result = AudioOutputUnitStop(au_rec_);
        if (0 != result) {
            error("audio_io_osx: Error stopping Audio Unit (result=%d) \n", result);
        }
        result = AudioComponentInstanceDispose(au_rec_);
        if (0 != result) {
            error("audio_io_osx: Error disposing Audio Unit (result=%d) \n", result);
        }
        au_rec_ = NULL;
    }
    if (NULL != au_play_) {
        result = AudioOutputUnitStop(au_play_);
        if (0 != result) {
            error("audio_io_osx: Error stopping Audio Unit (result=%d) \n", result);
        }
        result = AudioComponentInstanceDispose(au_play_);
        if (0 != result) {
            error("audio_io_osx: Error disposing Audio Unit (result=%d) \n", result);
        }
        au_play_ = NULL;
    }
    return 0;
}

static Fixed lsr_translate_scale(GF_LASeRCodec *lsr, u32 val)
{
	if (val >> (lsr->coord_bits-1) ) {
		s32 v = val - (1<<lsr->coord_bits);
		return INT2FIX(v) / 256 ;
	} else {
		return INT2FIX(val) / 256;
	}
}

static void *create_core_server_config(apr_pool_t *a, server_rec *s)
{
