set_cs_start(const char* line)
{
    static int cs_start_set = 0;
    char *p, *q, *r;
    if ((p = strstr(line, "string currentfile"))
        && strstr(line, "readstring")) {
        for (q = p; q != line && q[-1] != '/'; --q)
            ;
        if (q != line) {
            for (r = q; r != p && !isspace((unsigned char) *r) && *r != '{'; ++r)
                ;
            if (cs_start_set)
                free((char*) cs_start);
            cs_start = p = malloc(r - q + 1);
            memcpy(p, q, r - q);
            p[r - q] = 0;
            cs_start_set = 1;
        }
    }
}

static inline struct sem_array *sem_obtain_lock(struct ipc_namespace *ns, int id)
{
	struct kern_ipc_perm *ipcp;
	struct sem_array *sma;
	rcu_read_lock();
	ipcp = ipc_obtain_object(&sem_ids(ns), id);
	if (IS_ERR(ipcp)) {
		sma = ERR_CAST(ipcp);
		goto err;
	}
	spin_lock(&ipcp->lock);
	if (!ipcp->deleted)
		return container_of(ipcp, struct sem_array, sem_perm);
	spin_unlock(&ipcp->lock);
	sma = ERR_PTR(-EINVAL);
err:
	rcu_read_unlock();
	return sma;
}

static void vgacon_scrollback_init(int vc_num)
{
	int pitch = vga_video_num_columns * 2;
	size_t size = CONFIG_VGACON_SOFT_SCROLLBACK_SIZE * 1024;
	int rows = size / pitch;
	void *data;
	data = kmalloc_array(CONFIG_VGACON_SOFT_SCROLLBACK_SIZE, 1024,
			     GFP_NOWAIT);
	vgacon_scrollbacks[vc_num].data = data;
	vgacon_scrollback_cur = &vgacon_scrollbacks[vc_num];
