void DoRealForwardFFT(OpKernelContext* ctx, uint64* fft_shape,
                      const Tensor& in, Tensor* out) {
  const auto axes = Eigen::ArrayXi::LinSpaced(FFTRank, 1, FFTRank);
  auto device = ctx->eigen_device<CPUDevice>();
  auto input = Tensor(in).flat_inner_dims<RealT, FFTRank + 1>();
  const auto input_dims = input.dimensions();
  Eigen::DSizes<Eigen::DenseIndex, FFTRank + 1> input_slice_sizes;
  input_slice_sizes[0] = input_dims[0];
  TensorShape temp_shape{input_dims[0]};
  for (int i = 1; i <= FFTRank; ++i) {
    input_slice_sizes[i] = fft_shape[i - 1];
    temp_shape.AddDim(fft_shape[i - 1]);
  }
  OP_REQUIRES(ctx, temp_shape.num_elements() > 0,
              errors::InvalidArgument("Obtained a FFT shape of 0 elements: ",
                                      temp_shape.DebugString()));
  auto output = out->flat_inner_dims<ComplexT, FFTRank + 1>();
  const Eigen::DSizes<Eigen::DenseIndex, FFTRank + 1> zero_start_indices;
  Tensor temp;
  OP_REQUIRES_OK(ctx, ctx->allocate_temp(DataTypeToEnum<ComplexT>::v(),
                                         temp_shape, &temp));
  auto full_fft = temp.flat_inner_dims<ComplexT, FFTRank + 1>();
  full_fft.device(device) =
      input.slice(zero_start_indices, input_slice_sizes)
          .template fft<Eigen::BothParts, Eigen::FFT_FORWARD>(axes);
  output.device(device) =
      full_fft.slice(zero_start_indices, output.dimensions());
}

create_principal3_2_svc(cprinc3_arg *arg, struct svc_req *rqstp)
{
    static generic_ret          ret;
    char                        *prime_arg;
    gss_buffer_desc             client_name, service_name;
    OM_uint32                   minor_stat;
    kadm5_server_handle_t       handle;
    restriction_t               *rp;
    const char                  *errmsg = NULL;
    xdr_free(xdr_generic_ret, &ret);
    if ((ret.code = new_server_handle(arg->api_version, rqstp, &handle)))
        goto exit_func;
    if ((ret.code = check_handle((void *)handle)))
        goto exit_func;
    ret.api_version = handle->api_version;
    if (setup_gss_names(rqstp, &client_name, &service_name) < 0) {
        ret.code = KADM5_FAILURE;
        goto exit_func;
    }
    if (krb5_unparse_name(handle->context, arg->rec.principal, &prime_arg)) {
        ret.code = KADM5_BAD_PRINCIPAL;
        goto exit_func;
    }
    if (CHANGEPW_SERVICE(rqstp)
        || !kadm5int_acl_check(handle->context, rqst2name(rqstp), ACL_ADD,
                               arg->rec.principal, &rp)
        || kadm5int_acl_impose_restrictions(handle->context,
                                            &arg->rec, &arg->mask, rp)) {
        ret.code = KADM5_AUTH_ADD;
        log_unauth("kadm5_create_principal", prime_arg,
                   &client_name, &service_name, rqstp);
    } else {
        ret.code = kadm5_create_principal_3((void *)handle,
                                            &arg->rec, arg->mask,
                                            arg->n_ks_tuple,
                                            arg->ks_tuple,
                                            arg->passwd);
        if( ret.code != 0 )
            errmsg = krb5_get_error_message(handle->context, ret.code);
        log_done("kadm5_create_principal", prime_arg, errmsg,
                 &client_name, &service_name, rqstp);
        if (errmsg != NULL)
            krb5_free_error_message(handle->context, errmsg);
    }
    free(prime_arg);
    gss_release_buffer(&minor_stat, &client_name);
    gss_release_buffer(&minor_stat, &service_name);
exit_func:
    free_server_handle(handle);
    return &ret;
}

static void cleanup_timers(struct list_head *head,
			   cputime_t utime, cputime_t stime,
			   unsigned long long sum_exec_runtime)
{
	struct cpu_timer_list *timer, *next;
	cputime_t ptime = cputime_add(utime, stime);
	list_for_each_entry_safe(timer, next, head, entry) {
		list_del_init(&timer->entry);
		if (cputime_lt(timer->expires.cpu, ptime)) {
			timer->expires.cpu = cputime_zero;
		} else {
			timer->expires.cpu = cputime_sub(timer->expires.cpu,
							 ptime);
		}
	}
	++head;
	list_for_each_entry_safe(timer, next, head, entry) {
		list_del_init(&timer->entry);
		if (cputime_lt(timer->expires.cpu, utime)) {
			timer->expires.cpu = cputime_zero;
		} else {
			timer->expires.cpu = cputime_sub(timer->expires.cpu,
							 utime);
		}
	}
	++head;
	list_for_each_entry_safe(timer, next, head, entry) {
		list_del_init(&timer->entry);
		if (timer->expires.sched < sum_exec_runtime) {
			timer->expires.sched = 0;
		} else {
			timer->expires.sched -= sum_exec_runtime;
		}
	}
}

void ModifiablePixelBuffer::fillRect(const Rect& r, const void* pix)
{
  int stride;
  U8 *buf;
  int w, h, b;
  w = r.width();
  h = r.height();
  b = format.bpp/8;
  if (h == 0)
    return;
  buf = getBufferRW(r, &stride);
  if (b == 1) {
    while (h--) {
      memset(buf, *(const U8*)pix, w);
      buf += stride * b;
    }
  } else {
    U8 *start;
    int w1;
    start = buf;
    w1 = w;
    while (w1--) {
      memcpy(buf, pix, b);
      buf += b;
    }
    buf += (stride - w) * b;
    h--;
    while (h--) {
      memcpy(buf, start, w * b);
      buf += stride * b;
    }
  }
  commitBufferRW(r);
}

