int FixCom::run(const std::string& path)
{
    try {
        if (!Exiv2::fileExists(path, true)) {
            std::cerr << path << ": " << _("Failed to open the file\n");
            return -1;
        }
        Timestamp ts;
        if (Params::instance().preserve_)
            ts.read(path);
        Exiv2::Image::UniquePtr image = Exiv2::ImageFactory::open(path);
        image->readMetadata();
        Exiv2::ExifData& exifData = image->exifData();
        if (exifData.empty()) {
            std::cerr << path << ": " << _("No Exif data found in the file\n");
            return -3;
        }
        auto pos = exifData.findKey(Exiv2::ExifKey("Exif.Photo.UserComment"));
        if (pos == exifData.end()) {
            if (Params::instance().verbose_) {
                std::cout << _("No Exif user comment found") << "\n";
            }
            return 0;
        }
        Exiv2::Value::UniquePtr v = pos->getValue();
        const Exiv2::CommentValue* pcv = dynamic_cast<const Exiv2::CommentValue*>(v.get());
        if (!pcv) {
            if (Params::instance().verbose_) {
                std::cout << _("Found Exif user comment with unexpected value type") << "\n";
            }
            return 0;
        }
        Exiv2::CommentValue::CharsetId csId = pcv->charsetId();
        if (csId != Exiv2::CommentValue::unicode) {
            if (Params::instance().verbose_) {
                std::cout << _("No Exif UNICODE user comment found") << "\n";
            }
            return 0;
        }
        std::string comment = pcv->comment(Params::instance().charset_.c_str());
        if (Params::instance().verbose_) {
            std::cout << _("Setting Exif UNICODE user comment to") << " \"" << comment << "\"\n";
        }
        comment = std::string("charset=\"") + Exiv2::CommentValue::CharsetInfo::name(csId) + "\" " + comment;
        pos->setValue(comment);
        image->writeMetadata();
        if (Params::instance().preserve_)
            ts.touch(path);
        return 0;
    } catch (const Exiv2::AnyError& e) {
        std::cerr << "Exiv2 exception in fixcom action for file " << path << ":\n" << e << "\n";
        return 1;
    }
}

void onComplete(const Status& status, ContextImpl& context) const override {
  auto& completion_state = context.getCompletionState(this);
  if (completion_state.is_completed_) {
    return;
  }
  if (Status::Ok == status) {
    completion_state.is_completed_ = true;
    completeWithStatus(status, context);
    return;
  }
  if (++completion_state.number_completed_children_ == verifiers_.size()) {
    Status final_status = Status::JwtMissed;
    for (const auto& it : verifiers_) {
      Status child_status = context.getCompletionState(it.get()).status_;
      if (child_status != Status::JwtMissed && child_status != Status::JwtUnknownIssuer) {
        final_status = child_status;
      }
    }
    if (is_allow_missing_or_failed_) {
      final_status = Status::Ok;
    } else if (is_allow_missing_ && final_status == Status::JwtMissed) {
      final_status = Status::Ok;
    }
    completion_state.is_completed_ = true;
    completeWithStatus(final_status, context);
  }
}

void __init xen_init_IRQ(void)
{
	int ret = -EINVAL;
	evtchn_port_t evtchn;
	if (fifo_events)
		ret = xen_evtchn_fifo_init();
	if (ret < 0)
		xen_evtchn_2l_init();
	xen_cpu_init_eoi(smp_processor_id());
	cpuhp_setup_state_nocalls(CPUHP_XEN_EVTCHN_PREPARE,
				  "xen/evtchn:prepare",
				  xen_evtchn_cpu_prepare, xen_evtchn_cpu_dead);
	evtchn_to_irq = kcalloc(EVTCHN_ROW(xen_evtchn_max_channels()),
				sizeof(*evtchn_to_irq), GFP_KERNEL);
	BUG_ON(!evtchn_to_irq);
	for (evtchn = 0; evtchn < xen_evtchn_nr_channels(); evtchn++)
		mask_evtchn(evtchn);
	pirq_needs_eoi = pirq_needs_eoi_flag;
#ifdef CONFIG_X86
	if (xen_pv_domain()) {
		if (xen_initial_domain())
			pci_xen_initial_domain();
	}
	if (xen_feature(XENFEAT_hvm_callback_vector)) {
		xen_setup_callback_vector();
		xen_alloc_callback_vector();
	}
	if (xen_hvm_domain()) {
		native_init_IRQ();
		pci_xen_hvm_init();
	} else {
		int rc;
		struct physdev_pirq_eoi_gmfn eoi_gmfn;
		pirq_eoi_map = (void *)__get_free_page(GFP_KERNEL|__GFP_ZERO);
		eoi_gmfn.gmfn = virt_to_gfn(pirq_eoi_map);
		rc = HYPERVISOR_physdev_op(PHYSDEVOP_pirq_eoi_gmfn_v2, &eoi_gmfn);
		if (rc != 0) {
			free_page((unsigned long) pirq_eoi_map);
			pirq_eoi_map = NULL;
		} else
			pirq_needs_eoi = pirq_check_eoi_map;
	}
#endif
}

