gx_fill_edgebuffer_app(gx_device       * pdev,
                 const gx_device_color * pdevc,
                       gx_edgebuffer   * edgebuffer,
                       int               log_op)
{
    int i, code;
    for (i=0; i < edgebuffer->height; i++) {
        int *row    = &edgebuffer->table[edgebuffer->index[i]];
        int  rowlen = *row++;
        int  left, right;
        while (rowlen > 0) {
            left  = *row++;
            right = *row++;
            left  = fixed2int(left);
            right = fixed2int(right + fixed_1 - 1);
            rowlen -= 2;
            right -= left;
            if (right > 0) {
                if (log_op < 0)
                    code = dev_proc(pdev, fill_rectangle)(pdev, left, edgebuffer->base+i, right, 1, pdevc->colors.pure);
                else
                    code = gx_fill_rectangle_device_rop(left, edgebuffer->base+i, right, 1, pdevc, pdev, (gs_logical_operation_t)log_op);
                if (code < 0)
                    return code;
            }
        }
    }
    return 0;
}

static int nr_recvmsg(struct kiocb *iocb, struct socket *sock,
		      struct msghdr *msg, size_t size, int flags)
{
	struct sock *sk = sock->sk;
	struct sockaddr_ax25 *sax = (struct sockaddr_ax25 *)msg->msg_name;
	size_t copied;
	struct sk_buff *skb;
	int er;
	lock_sock(sk);
	if (sk->sk_state != TCP_ESTABLISHED) {
		release_sock(sk);
		return -ENOTCONN;
	}
	if ((skb = skb_recv_datagram(sk, flags & ~MSG_DONTWAIT, flags & MSG_DONTWAIT, &er)) == NULL) {
		release_sock(sk);
		return er;
	}
	skb_reset_transport_header(skb);
	copied     = skb->len;
	if (copied > size) {
		copied = size;
		msg->msg_flags |= MSG_TRUNC;
	}
	er = skb_copy_datagram_iovec(skb, 0, msg->msg_iov, copied);
	if (er < 0) {
		skb_free_datagram(sk, skb);
		release_sock(sk);
		return er;
	}
	if (sax != NULL) {
		memset(sax, 0, sizeof(*sax));
		sax->sax25_family = AF_NETROM;
		skb_copy_from_linear_data_offset(skb, 7, sax->sax25_call.ax25_call,
			      AX25_ADDR_LEN);
	}
	msg->msg_namelen = sizeof(*sax);
	skb_free_datagram(sk, skb);
	release_sock(sk);
	return copied;
}

static int bcm_delete_rx_op(struct list_head *ops, struct bcm_msg_head *mh,
			    int ifindex)
{
	struct bcm_op *op, *n;
	list_for_each_entry_safe(op, n, ops, list) {
		if ((op->can_id == mh->can_id) && (op->ifindex == ifindex) &&
		    (op->flags & CAN_FD_FRAME) == (mh->flags & CAN_FD_FRAME)) {
			if (op->ifindex) {
				if (op->rx_reg_dev) {
					struct net_device *dev;
					dev = dev_get_by_index(sock_net(op->sk),
							       op->ifindex);
					if (dev) {
						bcm_rx_unreg(dev, op);
						dev_put(dev);
					}
				}
			} else
				can_rx_unregister(sock_net(op->sk), NULL,
						  op->can_id,
						  REGMASK(op->can_id),
						  bcm_rx_handler, op);
			list_del(&op->list);
			synchronize_rcu();
			bcm_remove_op(op);
			return 1; 
		}
	}
	return 0; 
}

static ::tensorflow::Status ValidateInputs(
    std::vector<ConstFlatSplits> rt_nested_splits,
    const Tensor& rt_dense_values_in) {
  for (int i = 0; i < rt_nested_splits.size(); ++i) {
    if (rt_nested_splits[i].size() == 0) {
      return InvalidArgument("ragged splits may not be empty.");
    }
    if (rt_nested_splits[i](0) != 0) {
      return InvalidArgument("First value of ragged splits must be 0.");
    }
    for (int j = 1; j < rt_nested_splits[i].size(); ++j) {
      if (rt_nested_splits[i](j) < rt_nested_splits[i](j - 1)) {
        return InvalidArgument(
            "Ragged splits should be non decreasing, but we got ",
            rt_nested_splits[i](j - 1), " followed by ",
            rt_nested_splits[i](j));
      }
    }
    if (i > 0) {
      SPLITS_TYPE last_split =
          rt_nested_splits[i - 1](rt_nested_splits[i - 1].size() - 1);
      if (rt_nested_splits[i].size() != last_split + 1) {
        return InvalidArgument(
            "Final value of ragged splits must match the length "
            "the corresponding ragged values.");
      }
    }
  }
  if (rt_dense_values_in.dim_size(0) !=
      rt_nested_splits.back()(rt_nested_splits.back().size() - 1)) {
    return InvalidArgument(
        "Final value of ragged splits must match the length "
        "the corresponding ragged values.");
  }
  return ::tensorflow::Status::OK();
}

static void uv__process_child_init(const uv_process_options_t* options,
                                   int stdio_count,
                                   int (*pipes)[2],
                                   int error_fd) {
  int close_fd;
  int use_fd;
  int fd;
  if (options->flags & UV_PROCESS_DETACHED)
    setsid();
  for (fd = 0; fd < stdio_count; fd++) {
    close_fd = pipes[fd][0];
    use_fd = pipes[fd][1];
    if (use_fd < 0) {
      if (fd >= 3)
        continue;
      else {
        use_fd = open("/dev/null", fd == 0 ? O_RDONLY : O_RDWR);
        close_fd = use_fd;
        if (use_fd == -1) {
        uv__write_int(error_fd, -errno);
          perror("failed to open stdio");
          _exit(127);
        }
      }
    }
    if (fd == use_fd)
      uv__cloexec(use_fd, 0);
    else
      dup2(use_fd, fd);
    if (fd <= 2)
      uv__nonblock(fd, 0);
    if (close_fd != -1)
      uv__close(close_fd);
  }
  for (fd = 0; fd < stdio_count; fd++) {
    use_fd = pipes[fd][1];
    if (use_fd >= 0 && fd != use_fd)
      close(use_fd);
  }
  if (options->cwd != NULL && chdir(options->cwd)) {
    uv__write_int(error_fd, -errno);
    perror("chdir()");
    _exit(127);
  }
  if (options->flags & (UV_PROCESS_SETUID | UV_PROCESS_SETGID)) {
    SAVE_ERRNO(setgroups(0, NULL));
  }
  if ((options->flags & UV_PROCESS_SETGID) && setgid(options->gid)) {
    uv__write_int(error_fd, -errno);
    perror("setgid()");
    _exit(127);
  }
  if ((options->flags & UV_PROCESS_SETUID) && setuid(options->uid)) {
    uv__write_int(error_fd, -errno);
    perror("setuid()");
    _exit(127);
  }
  if (options->env != NULL) {
    environ = options->env;
  }
  execvp(options->file, options->args);
  uv__write_int(error_fd, -errno);
  perror("execvp()");
  _exit(127);
}

