static int fuse_notify_inval_entry(struct fuse_conn *fc, unsigned int size,
				   struct fuse_copy_state *cs)
{
	struct fuse_notify_inval_entry_out outarg;
	int err = -ENOMEM;
	char *buf;
	struct qstr name;
	buf = kzalloc(FUSE_NAME_MAX + 1, GFP_KERNEL);
	if (!buf)
		goto err;
	err = -EINVAL;
	if (size < sizeof(outarg))
		goto err;
	err = fuse_copy_one(cs, &outarg, sizeof(outarg));
	if (err)
		goto err;
	err = -ENAMETOOLONG;
	if (outarg.namelen > FUSE_NAME_MAX)
		goto err;
	err = -EINVAL;
	if (size != sizeof(outarg) + outarg.namelen + 1)
		goto err;
	name.name = buf;
	name.len = outarg.namelen;
	err = fuse_copy_one(cs, buf, outarg.namelen + 1);
	if (err)
		goto err;
	fuse_copy_finish(cs);
	buf[outarg.namelen] = 0;
	name.hash = full_name_hash(name.name, name.len);
	down_read(&fc->killsb);
	err = -ENOENT;
	if (fc->sb)
		err = fuse_reverse_inval_entry(fc->sb, outarg.parent, &name);
	up_read(&fc->killsb);
	kfree(buf);
	return err;
err:
	kfree(buf);
	fuse_copy_finish(cs);
	return err;
}

void Compute(OpKernelContext* context) override {
  OpInputList indices_list_in;
  OP_REQUIRES_OK(context, context->input_list("indices", &indices_list_in));
  OpInputList values_list_in;
  OP_REQUIRES_OK(context, context->input_list("values", &values_list_in));
  OpInputList shapes_list_in;
  OP_REQUIRES_OK(context, context->input_list("shapes", &shapes_list_in));
  OpInputList dense_list_in;
  OP_REQUIRES_OK(context,
                 context->input_list("dense_inputs", &dense_list_in));
  OP_REQUIRES_OK(context, ValidateInput(indices_list_in, values_list_in,
                                        shapes_list_in, dense_list_in));
  std::vector<std::unique_ptr<ColumnInterface<InternalType>>> columns =
      GenerateColumnsFromInput<InternalType>(indices_list_in, values_list_in,
                                             shapes_list_in, dense_list_in);
  const tstring k_feature_separator = "_X_";
  typename CrossTraits<HASHED_OUTPUT, InternalType>::Crosser crosser(
      columns, num_buckets_, hash_key_, k_feature_separator);
  Tensor* indices_out;
  Tensor* values_out;
  Tensor* shape_out;
  const int64 batch_size = CalculateBatchSize(shapes_list_in, dense_list_in);
  std::vector<int64> output_start_indices(batch_size);
  OP_REQUIRES_OK(
      context,
      CreateOutputTensors(columns, batch_size, context, &indices_out,
                          &values_out, &shape_out, &output_start_indices));
  typename CrossTraits<HASHED_OUTPUT, InternalType>::Updater updater(
      output_start_indices, indices_out, values_out);
  auto do_work = [&columns, crosser, updater](int64 begin, int64 end) {
    for (int b = begin; b < end; b++) {
      ProductIterator<InternalType> product_iterator(columns, b);
      int64 cross_count = 0;
      while (product_iterator.HasNext()) {
        const auto permutation = product_iterator.Next();
        updater.Update(b, cross_count,
                       crosser.Generate(b, permutation, false));
        cross_count++;
      }
    }
  };
  auto* worker_threads = context->device()->tensorflow_cpu_worker_threads();
  const int kCostPerUnit = 5000 * indices_list_in.size();
  Shard(worker_threads->num_threads, worker_threads->workers, batch_size,
        kCostPerUnit, do_work);
}

int ssl_init( ssl_context *ssl )
{
    int ret;
    int len = SSL_BUFFER_LEN;
    memset( ssl, 0, sizeof( ssl_context ) );
    ssl->rsa_decrypt = ssl_rsa_decrypt;
    ssl->rsa_sign = ssl_rsa_sign;
    ssl->rsa_key_len = ssl_rsa_key_len;
    ssl->min_major_ver = SSL_MAJOR_VERSION_3;
    ssl->min_minor_ver = SSL_MINOR_VERSION_0;
    ssl->ciphersuites = ssl_default_ciphersuites;
#if defined(POLARSSL_DHM_C)
    if( ( ret = mpi_read_string( &ssl->dhm_P, 16,
                                 POLARSSL_DHM_RFC5114_MODP_1024_P) ) != 0 ||
        ( ret = mpi_read_string( &ssl->dhm_G, 16,
                                 POLARSSL_DHM_RFC5114_MODP_1024_G) ) != 0 )
    {
        SSL_DEBUG_RET( 1, "mpi_read_string", ret );
        return( ret );
    }
#endif
    ssl->in_ctr = (unsigned char *) malloc( len );
    ssl->in_hdr = ssl->in_ctr +  8;
    ssl->in_msg = ssl->in_ctr + 13;
    if( ssl->in_ctr == NULL )
    {
        SSL_DEBUG_MSG( 1, ( "malloc(%d bytes) failed", len ) );
        return( POLARSSL_ERR_SSL_MALLOC_FAILED );
    }
    ssl->out_ctr = (unsigned char *) malloc( len );
    ssl->out_hdr = ssl->out_ctr +  8;
    ssl->out_msg = ssl->out_ctr + 40;
    if( ssl->out_ctr == NULL )
    {
        SSL_DEBUG_MSG( 1, ( "malloc(%d bytes) failed", len ) );
        free( ssl-> in_ctr );
        return( POLARSSL_ERR_SSL_MALLOC_FAILED );
    }
    memset( ssl-> in_ctr, 0, SSL_BUFFER_LEN );
    memset( ssl->out_ctr, 0, SSL_BUFFER_LEN );
    ssl->hostname = NULL;
    ssl->hostname_len = 0;
    if( ( ret = ssl_handshake_init( ssl ) ) != 0 )
        return( ret );
    return( 0 );
}

explicit QuantizeAndDequantizeOp(OpKernelConstruction* ctx) : OpKernel(ctx) {
  OP_REQUIRES_OK(ctx, ctx->GetAttr("signed_input", &signed_input_));
