void Compute(OpKernelContext* ctx) override {
  auto x = ctx->input(0);
  auto i = ctx->input(1);
  auto v = ctx->input(2);
  OP_REQUIRES(ctx, TensorShapeUtils::IsVector(i.shape()),
              errors::InvalidArgument("i must be a vector. ",
                                      i.shape().DebugString()));
  OP_REQUIRES(ctx, x.dims() == v.dims(),
              errors::InvalidArgument(
                  "x and v shape doesn't match (ranks differ): ",
                  x.shape().DebugString(), " vs. ", v.shape().DebugString()));
  for (int i = 1; i < x.dims(); ++i) {
    OP_REQUIRES(
        ctx, x.dim_size(i) == v.dim_size(i),
        errors::InvalidArgument("x and v shape doesn't match at index ", i,
                                " : ", x.shape().DebugString(), " vs. ",
                                v.shape().DebugString()));
  }
  OP_REQUIRES(ctx, i.dim_size(0) == v.dim_size(0),
              errors::InvalidArgument(
                  "i and x shape doesn't match at index 0: ",
                  i.shape().DebugString(), " vs. ", v.shape().DebugString()));
  Tensor y = x;  
  if (x.NumElements() > 0 || v.NumElements() > 0) {
    OP_REQUIRES_OK(ctx, DoCompute(ctx, i, v, &y));
  }
  ctx->set_output(0, y);
}

static int snmp6_alloc_dev(struct inet6_dev *idev)
{
	if (snmp_mib_init((void __percpu **)idev->stats.ipv6,
			  sizeof(struct ipstats_mib),
			  __alignof__(struct ipstats_mib)) < 0)
		goto err_ip;
	idev->stats.icmpv6dev = kzalloc(sizeof(struct icmpv6_mib_device),
					GFP_KERNEL);
	if (!idev->stats.icmpv6dev)
		goto err_icmp;
	idev->stats.icmpv6msgdev = kzalloc(sizeof(struct icmpv6msg_mib_device),
					   GFP_KERNEL);
	if (!idev->stats.icmpv6msgdev)
		goto err_icmpmsg;
	return 0;
err_icmpmsg:
	kfree(idev->stats.icmpv6dev);
err_icmp:
	snmp_mib_free((void __percpu **)idev->stats.ipv6);
err_ip:
	return -ENOMEM;
}

GopherStateData::DelayAwareRead(GopherStateData *gopherState)
{
    const auto &conn = gopherState->serverConn;
    if (!Comm::IsConnOpen(conn) || fd_table[conn->fd].closing()) {
        debugs(10, 3, "will not read from " << conn);
        return;
    }
    const auto amountToRead = gopherState->entry->bytesWanted(Range<size_t>(0, BUFSIZ));
    if (amountToRead <= 0) {
        AsyncCall::Pointer delayCall = asyncCall(10, 3, "GopherStateData::DelayAwareRead",
                                       cbdataDialer(&GopherStateData::DelayAwareRead, gopherState));
        gopherState->entry->mem().delayRead(delayCall);
        return;
    }
    AsyncCall::Pointer readCall = commCbCall(5, 5, "gopherReadReply", CommIoCbPtrFun(gopherReadReply, gopherState));
    comm_read(conn, gopherState->replybuf, amountToRead, readCall);
}

void Compute(OpKernelContext* context) override {
  const Tensor& boxes = context->input(0);
  const Tensor& scores = context->input(1);
  const Tensor& max_output_size = context->input(2);
  OP_REQUIRES(
      context, TensorShapeUtils::IsScalar(max_output_size.shape()),
      errors::InvalidArgument("max_output_size must be 0-D, got shape ",
                              max_output_size.shape().DebugString()));
  const Tensor& iou_threshold = context->input(3);
  OP_REQUIRES(context, TensorShapeUtils::IsScalar(iou_threshold.shape()),
              errors::InvalidArgument("iou_threshold must be 0-D, got shape ",
                                      iou_threshold.shape().DebugString()));
  const T iou_threshold_val = iou_threshold.scalar<T>()();
  OP_REQUIRES(context,
              iou_threshold_val >= static_cast<T>(0.0) &&
                  iou_threshold_val <= static_cast<T>(1.0),
              errors::InvalidArgument("iou_threshold must be in [0, 1]"));
  const Tensor& score_threshold = context->input(4);
  OP_REQUIRES(
      context, TensorShapeUtils::IsScalar(score_threshold.shape()),
      errors::InvalidArgument("score_threshold must be 0-D, got shape ",
                              score_threshold.shape().DebugString()));
  const T score_threshold_val = score_threshold.scalar<T>()();
  int num_boxes = 0;
  ParseAndCheckBoxSizes(context, boxes, &num_boxes);
  CheckScoreSizes(context, num_boxes, scores);
  if (!context->status().ok()) {
    return;
  }
  auto similarity_fn = CreateIOUSimilarityFn<T>(boxes);
  int num_valid_outputs;
  bool return_scores_tensor_ = false;
  const T dummy_soft_nms_sigma = static_cast<T>(0.0);
  DoNonMaxSuppressionOp<T>(
      context, scores, num_boxes, max_output_size, iou_threshold_val,
      score_threshold_val, dummy_soft_nms_sigma, similarity_fn,
      return_scores_tensor_, pad_to_max_output_size_, &num_valid_outputs);
  if (!context->status().ok()) {
    return;
  }
  Tensor* num_outputs_t = nullptr;
  OP_REQUIRES_OK(context, context->allocate_output(
                              1, tensorflow::TensorShape{}, &num_outputs_t));
  num_outputs_t->scalar<int32>().setConstant(num_valid_outputs);
}

void ExtractStreams(Archive &Arc,const wchar *FileName,bool TestMode)
{
  wchar FullName[NM+2];
  if (FileName[0]!=0 && FileName[1]==0)
  {
    wcsncpyz(FullName,L".\\",ASIZE(FullName));
    wcsncatz(FullName,FileName,ASIZE(FullName));
  }
  else
    wcsncpyz(FullName,FileName,ASIZE(FullName));
  wchar StreamName[NM];
  GetStreamNameNTFS(Arc,StreamName,ASIZE(StreamName));
  if (*StreamName!=':')
  {
    uiMsg(UIERROR_STREAMBROKEN,Arc.FileName,FileName);
    ErrHandler.SetErrorCode(RARX_CRC);
    return;
  }
  if (TestMode)
  {
    File CurFile;
    Arc.ReadSubData(NULL,&CurFile,true);
    return;
  }
  wcsncatz(FullName,StreamName,ASIZE(FullName));
  FindData fd;
  bool HostFound=FindFile::FastFind(FileName,&fd);
  if ((fd.FileAttr & FILE_ATTRIBUTE_READONLY)!=0)
    SetFileAttr(FileName,fd.FileAttr & ~FILE_ATTRIBUTE_READONLY);
  File CurFile;
  if (CurFile.WCreate(FullName) && Arc.ReadSubData(NULL,&CurFile,false))
    CurFile.Close();
  File HostFile;
  if (HostFound && HostFile.Open(FileName,FMF_OPENSHARED|FMF_UPDATE))
    SetFileTime(HostFile.GetHandle(),&fd.ftCreationTime,&fd.ftLastAccessTime,
                &fd.ftLastWriteTime);
  SetFileAttr(FileName,fd.FileAttr);
}

R_API int r_io_bank_write_to_submap_at(RIO *io, const ut32 bankid, ut64 addr, const ut8 *buf, int len) {
	RIOBank *bank = r_io_bank_get (io, bankid);
	r_return_val_if_fail (io && bank, -1);
	if (!len) {
		return 0;
	}
	RRBNode *node;
	if (bank->last_used && r_io_submap_contain (((RIOSubMap *)bank->last_used->data), addr)) {
		node = bank->last_used;
	} else {
		node = r_crbtree_find_node (bank->submaps, &addr, _find_sm_by_vaddr_cb, NULL);
		if (!node) {
			return 0;
		}
		bank->last_used = node;
	}
	RIOSubMap *sm = (RIOSubMap *)node->data;
	if (!r_io_submap_contain (sm, addr)) {
		return 0;
	}
	RIOMap *map = r_io_map_get_by_ref (io, &sm->mapref);
	if (!map || !(map->perm & R_PERM_W)) {
		return -1;
	}
	const int write_len = R_MIN (len, r_io_submap_to (sm) - addr + 1);
	const ut64 paddr = addr - r_io_map_from (map) + map->delta;
	return r_io_fd_write_at (io, map->fd, paddr, buf, write_len);
}

