void Compute(OpKernelContext* context) override {
  const Tensor& filter = context->input(1);
  const TensorShape& filter_shape = filter.shape();
  const Tensor& out_backprop = context->input(2);
  const TensorShape& out_backprop_shape = out_backprop.shape();
  TensorShape input_shape;
  if (takes_shape_) {
    const Tensor& input_sizes = context->input(0);
    OP_REQUIRES_OK(context, tensor::MakeShape(input_sizes, &input_shape));
  } else {
    input_shape = context->input(0).shape();
  }
  OP_REQUIRES(
      context, input_shape.dim_size(4) == filter_shape.dim_size(3),
      errors::InvalidArgument("input and filter_sizes must have the same "
                              "number of channels. Got ",
                              input_shape.dim_size(4), " for input and ",
                              filter_shape.dim_size(3), " for filter_sizes"));
  OP_REQUIRES(
      context, out_backprop_shape.dim_size(4) == filter_shape.dim_size(4),
      errors::InvalidArgument("out_backprop and filter_sizes must have the "
                              "same number of channels. Got ",
                              out_backprop_shape.dim_size(4),
                              " for out_backprop and ",
                              filter_shape.dim_size(4), " for filter_sizes"));
  ConvBackpropDimensions dims;
  OP_REQUIRES_OK(context, ConvBackpropComputeDimensions(
                              "Conv3DBackpropInputOp", 3,
                              input_shape, filter_shape, out_backprop_shape,
                              stride_, padding_, data_format_, &dims));
  Tensor* in_backprop;
  OP_REQUIRES_OK(context,
                 context->allocate_output(0, input_shape, &in_backprop));
  functor::CuboidConvolutionBackwardInput<Device, T>()(
      context->eigen_device<Device>(),
      in_backprop->tensor<T, 5>(),                     
      filter.tensor<T, 5>(),                           
      out_backprop.tensor<T, 5>(),                     
      static_cast<int>(dims.spatial_dims[0].stride),   
      static_cast<int>(dims.spatial_dims[1].stride),   
      static_cast<int>(dims.spatial_dims[2].stride));  
}

static const unsigned char *parse_object(cJSON *item, const unsigned char *value, const unsigned char **ep)
{
    cJSON *child = NULL;
    if (*value != '{')
    {
        *ep = value;
        goto fail;
    }
    item->type = cJSON_Object;
    value = skip(value + 1);
    if (*value == '}')
    {
        return value + 1;
    }
    child = cJSON_New_Item();
    item->child = child;
    if (!item->child)
    {
        goto fail;
    }
    value = skip(parse_string(child, skip(value), ep));
    if (!value)
    {
        goto fail;
    }
    child->string = child->valuestring;
    child->valuestring = NULL;
    if (*value != ':')
    {
        *ep = value;
        goto fail;
    }
    value = skip(parse_value(child, skip(value + 1), ep));
    if (!value)
    {
        goto fail;
    }
    while (*value == ',')
    {
        cJSON *new_item = NULL;
        if (!(new_item = cJSON_New_Item()))
        {
            goto fail;
        }
        child->next = new_item;
        new_item->prev = child;
        child = new_item;
        value = skip(parse_string(child, skip(value + 1), ep));
        if (!value)
        {
            goto fail;
        }
        child->string = child->valuestring;
        child->valuestring = NULL;
        if (*value != ':')
        {
            *ep = value;
            goto fail;
        }
        value = skip(parse_value(child, skip(value + 1), ep));
        if (!value)
        {
            goto fail;
        }
    }
    if (*value == '}')
    {
        return value + 1;
    }
    *ep = value;
fail:
    if (item->child != NULL)
    {
        cJSON_Delete(child);
        item->child = NULL;
    }
    return NULL;
}

_dbus_get_monotonic_time (long *tv_sec,
                          long *tv_usec)
{
#ifdef HAVE_MONOTONIC_CLOCK
  struct timespec ts;
  clock_gettime (CLOCK_MONOTONIC, &ts);
  if (tv_sec)
    *tv_sec = ts.tv_sec;
  if (tv_usec)
    *tv_usec = ts.tv_nsec / 1000;
#else
  struct timeval t;
  gettimeofday (&t, NULL);
  if (tv_sec)
    *tv_sec = t.tv_sec;
  if (tv_usec)
    *tv_usec = t.tv_usec;
#endif
}

static int hidp_setup_hid(struct hidp_session *session,
				struct hidp_connadd_req *req)
{
	struct hid_device *hid;
	int err;
	session->rd_data = kzalloc(req->rd_size, GFP_KERNEL);
	if (!session->rd_data)
		return -ENOMEM;
	if (copy_from_user(session->rd_data, req->rd_data, req->rd_size)) {
		err = -EFAULT;
		goto fault;
	}
	session->rd_size = req->rd_size;
	hid = hid_allocate_device();
	if (IS_ERR(hid)) {
		err = PTR_ERR(hid);
		goto fault;
	}
	session->hid = hid;
	hid->driver_data = session;
	hid->bus     = BUS_BLUETOOTH;
	hid->vendor  = req->vendor;
	hid->product = req->product;
	hid->version = req->version;
	hid->country = req->country;
	strncpy(hid->name, req->name, 128);
	snprintf(hid->phys, sizeof(hid->phys), "%pMR",
		 &bt_sk(session->ctrl_sock->sk)->src);
	snprintf(hid->uniq, sizeof(hid->uniq), "%pMR",
		 &bt_sk(session->ctrl_sock->sk)->dst);
	hid->dev.parent = &session->conn->dev;
	hid->ll_driver = &hidp_hid_driver;
	hid->hid_get_raw_report = hidp_get_raw_report;
	hid->hid_output_raw_report = hidp_output_raw_report;
	if (hid_ignore(hid)) {
		hid_destroy_device(session->hid);
		session->hid = NULL;
		return -ENODEV;
	}
	return 0;
fault:
	kfree(session->rd_data);
	session->rd_data = NULL;
	return err;
}

para_macro_process(uschar *p, uschar *q)
{
int length = q - p;
argstr **pp;
macrodef *md;
macroexe *me;
for (md = macrolist; md != NULL; md = md->next)
  {
  if (length == md->namelength && Ustrncmp(p, md->name, length) == 0) break;
  }
if (md == NULL)
  {
  error(23, length, p);
  (void)fprintf(outfile, "&");
  return p;
  }
me = misc_malloc(sizeof(macroexe));
me->prev = macrocurrent;
macrocurrent = me;
me->macro = md;
me->nextline = md->lines;
if (from_type_ptr >= FROM_TYPE_STACKSIZE - 1) error(32);  
from_type[++from_type_ptr] = FROM_MACRO;
me->args = NULL;
pp = &(me->args);
while (isspace(*(++q)));
while (*q != 0 && *q != ')')
  {
  argstr *as = misc_malloc(sizeof(argstr));
  as->next = NULL;
  *pp = as;
  pp = &(as->next);
  as->string = misc_readitem(q, US",)", &length, NULL, 0);
  q += length;
  if (*q == ',') while (isspace(*(++q)));
  }
if (*q != ')')
  {
  error(24, p);
  (void)fprintf(outfile, "&");
  return p;
  }
para_inline_macro++;
for (;;)
  {
  uschar buffer[INBUFFSIZE];
  read_process_macroline(macrocurrent->nextline->string, buffer);
  if (*buffer == '.')
    {
    dot_process(buffer);
    if (macrocurrent != me) break;
    }
  else
    {
    uschar *qq = buffer + Ustrlen(buffer);
    while (qq > buffer && isspace(qq[-1])) qq--;
    *qq = 0;
    para_process(buffer);
    }
  macrocurrent->nextline = macrocurrent->nextline->next;
  if (macrocurrent->nextline == NULL)
    {
    macroexe *temp = macrocurrent;
    macrocurrent = macrocurrent->prev;
    macro_free(temp);
    from_type_ptr--;
    break;
    }
  }
para_inline_macro--;
return q + 1;
}

static void spl_array_it_get_current_key(zend_object_iterator *iter, zval *key TSRMLS_DC) 
{
	spl_array_it       *iterator = (spl_array_it *)iter;
	spl_array_object   *object   = iterator->object;
	HashTable          *aht      = spl_array_get_hash_table(object, 0 TSRMLS_CC);
	if (object->ar_flags & SPL_ARRAY_OVERLOADED_KEY) {
		zend_user_it_get_current_key(iter, key TSRMLS_CC);
	} else {
		if (spl_array_object_verify_pos_ex(object, aht, "ArrayIterator::current(): " TSRMLS_CC) == FAILURE) {
			ZVAL_NULL(key);
		} else {
			zend_hash_get_current_key_zval_ex(aht, key, &object->pos);
		}
	}
}

