int socketio_setoption(CONCRETE_IO_HANDLE socket_io, const char* optionName, const void* value)
{
    int result;
    if (socket_io == NULL ||
        optionName == NULL ||
        value == NULL)
    {
        result = MU_FAILURE;
    }
    else
    {
        SOCKET_IO_INSTANCE* socket_io_instance = (SOCKET_IO_INSTANCE*)socket_io;
        if (strcmp(optionName, "tcp_keepalive") == 0)
        {
            result = setsockopt(socket_io_instance->socket, SOL_SOCKET, SO_KEEPALIVE, value, sizeof(int));
            if (result == -1) result = errno;
        }
        else if (strcmp(optionName, "tcp_keepalive_time") == 0)
        {
#ifdef __APPLE__
            result = setsockopt(socket_io_instance->socket, IPPROTO_TCP, TCP_KEEPALIVE, value, sizeof(int));
#else
            result = setsockopt(socket_io_instance->socket, SOL_TCP, TCP_KEEPIDLE, value, sizeof(int));
#endif
            if (result == -1) result = errno;
        }
        else if (strcmp(optionName, "tcp_keepalive_interval") == 0)
        {
            result = setsockopt(socket_io_instance->socket, SOL_TCP, TCP_KEEPINTVL, value, sizeof(int));
            if (result == -1) result = errno;
        }
        else if (strcmp(optionName, OPTION_NET_INT_MAC_ADDRESS) == 0)
        {
#ifdef __APPLE__
            LogError("option not supported.");
            result = MU_FAILURE;
#else
            if (strlen(value) == 0)
            {
                LogError("option value must be a valid mac address");
                result = MU_FAILURE;
            }
            else if ((socket_io_instance->target_mac_address = (char*)malloc(sizeof(char) * (strlen(value) + 1))) == NULL)
            {
                LogError("failed setting net_interface_mac_address option (malloc failed)");
                result = MU_FAILURE;
            }
            else
            {
                strcpy(socket_io_instance->target_mac_address, value);
                strtoup(socket_io_instance->target_mac_address);
                result = 0;
            }
#endif
        }
        else if (strcmp(optionName, OPTION_ADDRESS_TYPE) == 0)
        {
            result = socketio_setaddresstype_option(socket_io_instance, (const char*)value);
        }
        else
        {
            result = MU_FAILURE;
        }
    }
    return result;
}

void Compute(OpKernelContext* context) override {
  const Tensor& input = context->input(0);
  const TensorShape& input_shape = input.shape();
  const Tensor& out_backprop = context->input(2);
  const TensorShape& out_backprop_shape = out_backprop.shape();
  TensorShape filter_shape;
  if (takes_shape_) {
    const Tensor& filter_sizes = context->input(1);
    OP_REQUIRES_OK(context, TensorShapeUtils::MakeShape(
                                filter_sizes.vec<int32>(), &filter_shape));
  } else {
    filter_shape = context->input(1).shape();
  }
  OP_REQUIRES(
      context, input_shape.dim_size(4) == filter_shape.dim_size(3),
      errors::InvalidArgument("input and filter_sizes must have the same "
                              "number of channels. Got ",
                              input_shape.dim_size(4), " for input and ",
                              filter_shape.dim_size(3), " for filter_sizes"));
  OP_REQUIRES(
      context, out_backprop_shape.dim_size(4) == filter_shape.dim_size(4),
      errors::InvalidArgument("out_backprop and filter_sizes must have the "
                              "same number of channels. Got ",
                              out_backprop_shape.dim_size(4),
                              " for out_backprop and ",
                              filter_shape.dim_size(4), " for filter_sizes"));
  ConvBackpropDimensions dims;
  OP_REQUIRES_OK(context,
                 ConvBackpropComputeDimensions(
                     "Conv3DBackpropFilterOp", 3,
                     input_shape, filter_shape, out_backprop_shape, stride_,
                     padding_, data_format_, &dims));
  Tensor* filter_backprop;
  OP_REQUIRES_OK(context,
                 context->allocate_output(0, filter_shape, &filter_backprop));
  if (input_shape.num_elements() == 0) {
    filter_backprop->template flat<T>().setZero();
    return;
  }
  functor::CuboidConvolutionBackwardFilter<Device, T>()(
      context->eigen_device<Device>(),
      filter_backprop->tensor<T, 5>(),                 
      input.tensor<T, 5>(),                            
      out_backprop.tensor<T, 5>(),                     
      static_cast<int>(dims.spatial_dims[0].stride),   
      static_cast<int>(dims.spatial_dims[1].stride),   
      static_cast<int>(dims.spatial_dims[2].stride));  
}

