NAN_METHOD(DetectCharacterEncoding) {
	Nan::HandleScope scope;
	v8::Local<v8::Object> inputBuffer = info[0]->ToObject();
	UCharsetDetector *charsetDetector;
	const UCharsetMatch *charsetMatch;
	UErrorCode errorCode = U_ZERO_ERROR;
	charsetDetector = ucsdet_open(&errorCode);
	if(U_FAILURE(errorCode)) {
		Nan::ThrowError("Failed to open ICU charset detector.");
		return;
	}
	ucsdet_setText(
		charsetDetector,
		node::Buffer::Data(inputBuffer),
		node::Buffer::Length(inputBuffer),
		&errorCode
	);
	if(U_FAILURE(errorCode)) {
		Nan::ThrowError("Failed to set ICU charset detectors text.");
		ucsdet_close(charsetDetector);
		return;
	}
	charsetMatch = ucsdet_detect(charsetDetector, &errorCode);
	if(U_FAILURE(errorCode)) {
		Nan::ThrowError("Failed to detect charset.");
		ucsdet_close(charsetDetector);
		return;
	}
	if(charsetMatch == NULL) {
		info.GetReturnValue().Set(Nan::Null());
		ucsdet_close(charsetDetector);
		return;
	}
	const char *charsetName = ucsdet_getName(charsetMatch, &errorCode);
	if(U_FAILURE(errorCode)) {
		Nan::ThrowError("Failed to get name from charset match.");
		ucsdet_close(charsetDetector);
		return;
	}
	int32_t confidence = ucsdet_getConfidence(charsetMatch, &errorCode);
	if(U_FAILURE(errorCode)) {
		Nan::ThrowError("Failed to get confidence from charset match.");
		ucsdet_close(charsetDetector);
		return;
	}
	v8::Local<v8::Object> obj = Nan::New<v8::Object>();
	obj->Set(Nan::New<v8::String>("encoding").ToLocalChecked(), Nan::New<v8::String>(charsetName).ToLocalChecked());
	obj->Set(Nan::New<v8::String>("confidence").ToLocalChecked(), Nan::New<v8::Number>(confidence));
	info.GetReturnValue().Set(obj);
	ucsdet_close(charsetDetector);
}

void operator()(OpKernelContext* ctx, const Index num_segments,
                const TensorShape& segment_ids_shape,
                typename TTypes<Index>::ConstFlat segment_ids,
                const Index data_size, const T* data,
                typename TTypes<T, 2>::Tensor output) {
  if (output.size() == 0) {
    return;
  }
  GPUDevice d = ctx->template eigen_device<GPUDevice>();
  GpuLaunchConfig config = GetGpuLaunchConfig(output.size(), d);
  TF_CHECK_OK(GpuLaunchKernel(
      SetToValue<T>, config.block_count, config.thread_per_block, 0,
      d.stream(), output.size(), output.data(), InitialValueF()()));
  if (data_size == 0 || segment_ids_shape.num_elements() == 0) {
    return;
  }
  const Index input_outer_dim_size = segment_ids.dimension(0);
  const Index input_inner_dim_size = data_size / input_outer_dim_size;
  config = GetGpuLaunchConfig(data_size, d);
  TF_CHECK_OK(
      GpuLaunchKernel(UnsortedSegmentCustomKernel<T, Index, ReductionF>,
                      config.block_count, config.thread_per_block, 0,
                      d.stream(), input_outer_dim_size, input_inner_dim_size,
                      num_segments, segment_ids.data(), data, output.data()));
}

static bool get_user_list(struct torture_context *torture, char ***users)
{
	struct winbindd_request req;
	struct winbindd_response rep;
	char **u = NULL;
	uint32_t count;
	char name[256];
	const char *extra_data;
	ZERO_STRUCT(req);
	ZERO_STRUCT(rep);
	DO_STRUCT_REQ_REP(WINBINDD_LIST_USERS, &req, &rep);
	extra_data = (char *)rep.extra_data.data;
	torture_assert(torture, extra_data, "NULL extra data");
	for(count = 0;
	    next_token(&extra_data, name, ",", sizeof(name));
	    count++)
	{
		u = talloc_realloc(torture, u, char *, count + 2);
		u[count+1] = NULL;
		u[count] = talloc_strdup(u, name);
	}
	SAFE_FREE(rep.extra_data.data);
	*users = u;
	return true;
}

static int bpf_adj_delta_to_off(struct bpf_insn *insn, u32 pos, u32 delta,
				u32 curr, const bool probe_pass)
{
	const s32 off_min = S16_MIN, off_max = S16_MAX;
	s32 off = insn->off;
	if (curr < pos && curr + off + 1 > pos)
		off += delta;
	else if (curr > pos + delta && curr + off + 1 <= pos + delta)
		off -= delta;
	if (off < off_min || off > off_max)
		return -ERANGE;
	if (!probe_pass)
		insn->off = off;
	return 0;
}

