generate_ava(cms_context *cms, SECItem *der, CERTAVA *certava)
{
	ava ava;
	SECOidData *oid;
	void *arena = PORT_NewArena(DER_DEFAULT_CHUNKSIZE);
	if (arena == NULL)
		cnreterr(-1, cms, "could not create arena");
	void *real_arena = cms->arena;
	cms->arena = arena;
	oid = SECOID_FindOID(&certava->type);
	if (!oid) {
		save_port_err() {
			PORT_FreeArena(arena, PR_TRUE);
		}
		cms->arena = real_arena;
		cnreterr(-1, cms, "could not find OID");
	}
	int rc = generate_object_id(cms, &ava.type, oid->offset);
	if (rc < 0) {
		PORT_FreeArena(arena, PR_TRUE);
		cms->arena = real_arena;
		return -1;
	}
	memcpy(&ava.value, &certava->value, sizeof (ava.value));
	void *ret;
	SECItem tmp;
	ret = SEC_ASN1EncodeItem(arena, &tmp, &ava, AVATemplate);
	if (ret == NULL) {
		save_port_err() {
			PORT_FreeArena(arena, PR_TRUE);
		}
		cms->arena = real_arena;
		cnreterr(-1, cms, "could not encode AVA");
	}
	der->type = tmp.type;
	der->len = tmp.len;
	der->data = PORT_ArenaAlloc(real_arena, tmp.len);
	if (!der->data) {
		save_port_err() {
			PORT_FreeArena(arena, PR_TRUE);
		}
		cms->arena = real_arena;
		cnreterr(-1, cms, "could not allocate AVA");
	}
	memcpy(der->data, tmp.data, tmp.len);
	PORT_FreeArena(arena, PR_TRUE);
	cms->arena = real_arena;
	return 0;
}

static void nalm_dump(FILE * trace, char *data, u32 data_size)
{
	GF_BitStream *bs;
	Bool rle, large_size;
	u32 entry_count;
	if (!data) {
		fprintf(trace, "<NALUMap rle=\"\" large_size=\"\">\n");
		fprintf(trace, "<NALUMapEntry NALU_startNumber=\"\" groupID=\"\"/>\n");
		fprintf(trace, "</NALUMap>\n");
		return;
	}
	bs = gf_bs_new(data, data_size, GF_BITSTREAM_READ);
	gf_bs_read_int(bs, 6);
	large_size = gf_bs_read_int(bs, 1);
	rle = gf_bs_read_int(bs, 1);
	entry_count = gf_bs_read_int(bs, large_size ? 16 : 8);
	fprintf(trace, "<NALUMap rle=\"%d\" large_size=\"%d\">\n", rle, large_size);
	while (entry_count) {
		u32 ID;
		fprintf(trace, "<NALUMapEntry ");
		if (rle) {
			u32 start_num = gf_bs_read_int(bs, large_size ? 16 : 8);
			fprintf(trace, "NALU_startNumber=\"%d\" ", start_num);
		}
		ID = gf_bs_read_u16(bs);
		fprintf(trace, "groupID=\"%d\"/>\n", ID);
		entry_count--;
	}
	gf_bs_del(bs);
	fprintf(trace, "</NALUMap>\n");
	return;
}

void Compute(OpKernelContext* context) override {
  const float in_min = context->input(2).flat<float>()(0);
  const float in_max = context->input(3).flat<float>()(0);
  ImageResizerState st(align_corners_, false);
  st.ValidateAndCreateOutput(context);
  if (!context->status().ok()) return;
  if (st.output->NumElements() == 0) return;
  typename TTypes<T, 4>::ConstTensor image_data(
      context->input(0).tensor<T, 4>());
  typename TTypes<T, 4>::Tensor output_data(st.output->tensor<T, 4>());
  ResizeBilinear<T>(image_data, st.height_scale, st.width_scale, in_min,
                    in_max, half_pixel_centers_, &output_data);
  Tensor* out_min = nullptr;
  OP_REQUIRES_OK(context, context->allocate_output(1, {}, &out_min));
  out_min->flat<float>()(0) = in_min;
  Tensor* out_max = nullptr;
  OP_REQUIRES_OK(context, context->allocate_output(2, {}, &out_max));
  out_max->flat<float>()(0) = in_max;
}

