static int process_options(int argc, char *argv[], char *operation)
{
  int error= 0;
  int i= 0;
  if ((error= handle_options(&argc, &argv, my_long_options, get_one_option)))
    goto exit;
  if (opt_print_defaults)
  {
    error= -1;
    goto exit;
  }
  if (opt_basedir)
  {
    i= (int)strlength(opt_basedir);
    if (opt_basedir[i-1] != FN_LIBCHAR || opt_basedir[i-1] != FN_LIBCHAR2)
    {
      char buff[FN_REFLEN];
      memset(buff, 0, sizeof(buff));
      strncpy(buff, opt_basedir, sizeof(buff) - 1);
#ifdef __WIN__
      strncat(buff, "/", sizeof(buff) - strlen(buff) - 1);
#else
      strncat(buff, FN_DIRSEP, sizeof(buff) - strlen(buff) - 1);
#endif
      buff[sizeof(buff) - 1]= 0;
      my_free(opt_basedir);
      opt_basedir= my_strdup(buff, MYF(MY_FAE));
    }
  }
  if (!opt_no_defaults && ((error= get_default_values())))
  {
    error= -1;
    goto exit;
  }
  strcpy(operation, "");
  if ((error = check_options(argc, argv, operation)))
  {
    goto exit;
  }
  if (opt_verbose)
  {
    printf("#    basedir = %s\n", opt_basedir);
    printf("# plugin_dir = %s\n", opt_plugin_dir);
    printf("#    datadir = %s\n", opt_datadir);
    printf("# plugin_ini = %s\n", opt_plugin_ini);
  }
exit:
  return error;
}

int secure_check(void *data)
{
	const at91_secure_header_t *header;
	void *file;
	if (secure_decrypt(data, sizeof(*header), 0))
		return -1;
	header = (const at91_secure_header_t *)data;
	if (header->magic != AT91_SECURE_MAGIC)
		return -1;
	file = (unsigned char *)data + sizeof(*header);
	return secure_decrypt(file, header->file_size, 1);
}

unicode_log2vis (PyUnicodeObject* string,
                 FriBidiParType base_direction, int clean, int reordernsm)
{
    int i;
    int length = string->length;
    FriBidiChar *logical = NULL; 
    FriBidiChar *visual = NULL;      
    FriBidiStrIndex new_len = 0; 
    PyUnicodeObject *result = NULL;
    logical = PyMem_New (FriBidiChar, length + 1);
    if (logical == NULL) {
        PyErr_NoMemory();
        goto cleanup;
    }
    visual = PyMem_New (FriBidiChar, length + 1);
    if (visual == NULL) {
        PyErr_NoMemory();
        goto cleanup;
    }
    for (i=0; i<length; ++i) {
        logical[i] = string->str[i];
    }
    fribidi_set_reorder_nsm(reordernsm);
    if (!fribidi_log2vis (logical, length, &base_direction, visual,
                          NULL, NULL, NULL)) {
        PyErr_SetString (PyExc_RuntimeError,
                         "fribidi failed to order string");
        goto cleanup;
    }
    if (clean) {
        length = fribidi_remove_bidi_marks (visual, length, NULL, NULL, NULL);
    }
    result = (PyUnicodeObject*) PyUnicode_FromUnicode(NULL, length);
    if (result == NULL) {
        goto cleanup;
    }
    for (i=0; i<length; ++i) {
        result->str[i] = visual[i];
    }
  cleanup:
    PyMem_Del (logical);
    PyMem_Del (visual);
    return (PyObject *)result;
}

static inline FILE *xfmkstemp(char **tmpname, char *dir)
{
	int fd;
	FILE *ret;
	fd = xmkstemp(tmpname, dir);
	if (fd == -1)
		return NULL;
	if (!(ret = fdopen(fd, "w+" UL_CLOEXECSTR))) {
		close(fd);
		return NULL;
	}
	return ret;
}

