StreamBufferHandle_t xStreamBufferGenericCreate( size_t xBufferSizeBytes,
                                                 size_t xTriggerLevelBytes,
                                                 BaseType_t xIsMessageBuffer )
{
    uint8_t * pucAllocatedMemory;
    uint8_t ucFlags;
    if( xIsMessageBuffer == pdTRUE )
    {
        ucFlags = sbFLAGS_IS_MESSAGE_BUFFER;
        configASSERT( xBufferSizeBytes > sbBYTES_TO_STORE_MESSAGE_LENGTH );
    }
    else
    {
        ucFlags = 0;
        configASSERT( xBufferSizeBytes > 0 );
    }
    configASSERT( xTriggerLevelBytes <= xBufferSizeBytes );
    if( xTriggerLevelBytes == ( size_t ) 0 )
    {
        xTriggerLevelBytes = ( size_t ) 1;
    }
    xBufferSizeBytes++;
    pucAllocatedMemory = ( uint8_t * ) pvPortMalloc( xBufferSizeBytes + sizeof( StreamBuffer_t ) ); 
    if( pucAllocatedMemory != NULL )
    {
        prvInitialiseNewStreamBuffer( ( StreamBuffer_t * ) pucAllocatedMemory,         
                                      pucAllocatedMemory + sizeof( StreamBuffer_t ),  
                                      xBufferSizeBytes,
                                      xTriggerLevelBytes,
                                      ucFlags );
        traceSTREAM_BUFFER_CREATE( ( ( StreamBuffer_t * ) pucAllocatedMemory ), xIsMessageBuffer );
    }
    else
    {
        traceSTREAM_BUFFER_CREATE_FAILED( xIsMessageBuffer );
    }
    return ( StreamBufferHandle_t ) pucAllocatedMemory; 
}

bbuf_clone(BBuf** rto, BBuf* from)
{
  int r;
  BBuf *to;
  *rto = to = (BBuf* )xmalloc(sizeof(BBuf));
  CHECK_NULL_RETURN_MEMERR(to);
  r = BB_INIT(to, from->alloc);
  if (r != 0) {
    xfree(to->p);
    *rto = 0;
    return r;
  }
  to->used = from->used;
  xmemcpy(to->p, from->p, from->used);
  return 0;
}

ErrorCode HTTP2Codec::checkNewStream(uint32_t streamId, bool trailersAllowed) {
  if (streamId == 0) {
    goawayErrorMessage_ = folly::to<string>(
        "GOAWAY error: received streamID=", streamId,
        " as invalid new stream for lastStreamID_=", lastStreamID_);
    VLOG(4) << goawayErrorMessage_;
    return ErrorCode::PROTOCOL_ERROR;
  }
  parsingDownstreamTrailers_ = trailersAllowed && (streamId <= lastStreamID_);
  if (parsingDownstreamTrailers_) {
    VLOG(4) << "Parsing downstream trailers streamId=" << streamId;
  }
  if (sessionClosing_ != ClosingState::CLOSED) {
    lastStreamID_ = streamId;
  }
  if (isInitiatedStream(streamId)) {
    goawayErrorMessage_ = folly::to<string>(
        "GOAWAY error: invalid new stream received with streamID=", streamId);
    VLOG(4) << goawayErrorMessage_;
    return ErrorCode::PROTOCOL_ERROR;
  } else {
    return ErrorCode::NO_ERROR;
  }
}

nvmet_fc_find_target_queue(struct nvmet_fc_tgtport *tgtport,
				u64 connection_id)
{
	struct nvmet_fc_tgt_assoc *assoc;
	struct nvmet_fc_tgt_queue *queue;
	u64 association_id = nvmet_fc_getassociationid(connection_id);
	u16 qid = nvmet_fc_getqueueid(connection_id);
	unsigned long flags;
	if (qid > NVMET_NR_QUEUES)
		return NULL;
	spin_lock_irqsave(&tgtport->lock, flags);
	list_for_each_entry(assoc, &tgtport->assoc_list, a_list) {
		if (association_id == assoc->association_id) {
			queue = assoc->queues[qid];
			if (queue &&
			    (!atomic_read(&queue->connected) ||
			     !nvmet_fc_tgt_q_get(queue)))
				queue = NULL;
			spin_unlock_irqrestore(&tgtport->lock, flags);
			return queue;
		}
	}
	spin_unlock_irqrestore(&tgtport->lock, flags);
	return NULL;
}

