findNextBorderPixel(l_int32    w,
                    l_int32    h,
                    l_uint32  *data,
                    l_int32    wpl,
                    l_int32    px,
                    l_int32    py,
                    l_int32   *pqpos,
                    l_int32   *pnpx,
                    l_int32   *pnpy)
{
l_int32    qpos, i, pos, npx, npy, val;
l_uint32  *line;
    qpos = *pqpos;
    for (i = 1; i < 8; i++) {
        pos = (qpos + i) % 8;
        npx = px + xpostab[pos];
        npy = py + ypostab[pos];
        if (npx < 0 || npx >= w || npy < 0 || npy >= h)
            continue;
        line = data + npy * wpl;
        val = GET_DATA_BIT(line, npx);
        if (val) {
            *pnpx = npx;
            *pnpy = npy;
            *pqpos = qpostab[pos];
            return 0;
        }
    }
    return 1;
}

static void br_multicast_del_pg(struct net_bridge *br,
				struct net_bridge_port_group *pg)
{
	struct net_bridge_mdb_htable *mdb;
	struct net_bridge_mdb_entry *mp;
	struct net_bridge_port_group *p;
	struct net_bridge_port_group __rcu **pp;
	mdb = mlock_dereference(br->mdb, br);
	mp = br_mdb_ip_get(mdb, &pg->addr);
	if (WARN_ON(!mp))
		return;
	for (pp = &mp->ports;
	     (p = mlock_dereference(*pp, br)) != NULL;
	     pp = &p->next) {
		if (p != pg)
			continue;
		rcu_assign_pointer(*pp, p->next);
		hlist_del_init(&p->mglist);
		del_timer(&p->timer);
		call_rcu_bh(&p->rcu, br_multicast_free_pg);
		if (!mp->ports && !mp->mglist &&
		    netif_running(br->dev))
			mod_timer(&mp->timer, jiffies);
		return;
	}
	WARN_ON(1);
}

void Compute(OpKernelContext* ctx) override {
  const Tensor& val = ctx->input(0);
  int64 id = ctx->session_state()->GetNewId();
  TensorStore::TensorAndKey tk{val, id, requested_device()};
  OP_REQUIRES_OK(ctx, ctx->tensor_store()->AddTensor(name(), tk));
  Tensor* handle = nullptr;
  OP_REQUIRES_OK(ctx, ctx->allocate_output(0, TensorShape({}), &handle));
  if (ctx->expected_output_dtype(0) == DT_RESOURCE) {
    ResourceHandle resource_handle = MakeResourceHandle<Tensor>(
        ctx, SessionState::kTensorHandleResourceTypeName,
        tk.GetHandle(name()));
    resource_handle.set_maybe_type_name(
        SessionState::kTensorHandleResourceTypeName);
    handle->scalar<ResourceHandle>()() = resource_handle;
  } else {
    handle->flat<tstring>().setConstant(tk.GetHandle(name()));
  }
}

