aspath_loop_check (struct aspath *aspath, as_t asno)
{
  struct assegment *seg;
  int count = 0;
  if ( (aspath == NULL) || (aspath->segments == NULL) )
    return 0;
  seg = aspath->segments;
  while (seg)
    {
      int i;
      for (i = 0; i < seg->length; i++)
	if (seg->as[i] == asno)
	  count++;
      seg = seg->next;
    }
  return count;
}

