struct qmp *qmp_get(struct device *dev)
{
	struct platform_device *pdev;
	struct device_node *np;
	struct qmp *qmp;
	if (!dev || !dev->of_node)
		return ERR_PTR(-EINVAL);
	np = of_parse_phandle(dev->of_node, "qcom,qmp", 0);
	if (!np)
		return ERR_PTR(-ENODEV);
	pdev = of_find_device_by_node(np);
	of_node_put(np);
	if (!pdev)
		return ERR_PTR(-EINVAL);
	qmp = platform_get_drvdata(pdev);
	return qmp ? qmp : ERR_PTR(-EPROBE_DEFER);
}

static void addFrames(EctEnhancedCT *ct)
{
    if (!ct)
        return;
    FGFrameContent *fg = new FGFrameContent();
    fg->setStackID("1");
    OFCHECK(fg);
    if (fg)
    {
        EctEnhancedCT::FramesType frames = ct->getFrames();
        for (Uint16 frameNo = 1; frameNo <= NUM_FRAMES; frameNo++)
        {
            OFCHECK(fg->setFrameAcquisitionNumber(frameNo).good());
            OFCHECK(fg->setFrameReferenceDateTime("20190816092557").good());
            OFCHECK(fg->setFrameAcquisitionDateTime("20190816092557").good());
            OFCHECK(fg->setFrameAcquisitionDuration(0.001).good());
            OFCHECK(fg->setInStackPositionNumber(frameNo).good());
            OFCHECK(fg->setDimensionIndexValues(1, 0).good());
            OFCHECK(fg->setDimensionIndexValues(frameNo, 1).good());
            OFVector<FGBase *> groups;
            groups.push_back(fg);
            Uint16 *data = new Uint16[NUM_PIXELS_PER_FRAME];
            for (size_t i = 0; i < NUM_PIXELS_PER_FRAME; ++i)
            {
                data[i] = 0x4141;
            }
            OFCHECK(
                OFget<EctEnhancedCT::Frames<Uint16>>(&frames)->addFrame(data, NUM_PIXELS_PER_FRAME, groups).good());
            delete[] data;
        }
    }
    delete fg;
}

void Compute(OpKernelContext* context) override {
  const Tensor& input_sizes = context->input(0);
  const Tensor& filter = context->input(1);
  const Tensor& out_backprop = context->input(2);
  OP_REQUIRES(
      context, out_backprop.dims() == 4,
      errors::InvalidArgument("input_sizes must be 4-dimensional, got: ",
                              out_backprop.dims()));
  TensorShape input_shape;
  OP_REQUIRES_OK(context,
                 Conv2DBackpropComputeInputShape(input_sizes, filter.shape(),
                                                 out_backprop.shape(),
                                                 data_format_, &input_shape));
  Tensor* in_backprop = nullptr;
  OP_REQUIRES_OK(context,
                 context->allocate_output(0, input_shape, &in_backprop));
  if (input_shape.num_elements() == 0) {
    return;
  }
  if (out_backprop.NumElements() == 0) {
    functor::SetZeroFunctor<Device, T> set_zero;
    set_zero(context->eigen_device<Device>(),
             in_backprop->template flat<T>());
    return;
  }
  const int stride_rows = GetTensorDim(strides_, data_format_, 'H');
  const int stride_cols = GetTensorDim(strides_, data_format_, 'W');
  const int dilation_rows = GetTensorDim(dilations_, data_format_, 'H');
  const int dilation_cols = GetTensorDim(dilations_, data_format_, 'W');
  VLOG(2) << "Conv2DBackpropInput:"
          << " input: " << input_shape.DebugString()
          << " filter:" << filter.shape().DebugString()
          << " out_backprop: " << out_backprop.shape().DebugString()
          << " strides: [" << stride_rows << ", " << stride_cols << "]"
          << " dilations: [" << dilation_rows << ", " << dilation_cols << "]";
  LaunchConv2DBackpropInputOp<Device, T> launch;
  launch(context, use_cudnn_, cudnn_use_autotune_, out_backprop, filter,
         dilation_rows, dilation_cols, stride_rows, stride_cols, padding_,
         explicit_paddings_, in_backprop, data_format_);
}

