Status BuildFeatureReaders(const OpInputList& ragged_values_list,
                           const OpInputList& ragged_splits_list,
                           const OpInputList& sparse_indices_list,
                           const OpInputList& sparse_values_list,
                           const OpInputList& dense_list, int64 batch_size,
                           FeatureReaders* features) {
  features->reserve(input_order_.size());
  int next_ragged = 0;
  int next_sparse = 0;
  int next_dense = 0;
  for (char c : input_order_) {
    if (c == 'R') {
      if (next_ragged >= ragged_values_list.size())
        return errors::InvalidArgument(
            "input_order \"", input_order_,
            "\" specifies reading a ragged tensor value at index ",
            next_ragged, " from a list of ", ragged_values_list.size(),
            " values.");
      if (next_ragged >= ragged_splits_list.size())
        return errors::InvalidArgument(
            "input_order \"", input_order_,
            "\" specifies reading a ragged tensor split at index ",
            next_ragged, " from a list of ", ragged_splits_list.size(),
            " splits.");
      TF_RETURN_IF_ERROR(BuildRaggedFeatureReader(
          ragged_values_list[next_ragged], ragged_splits_list[next_ragged],
          features));
      next_ragged++;
    } else if (c == 'S') {
      if (next_sparse >= sparse_values_list.size())
        return errors::InvalidArgument(
            "input_order \"", input_order_,
            "\" specifies reading a sparse tensor value at index ",
            next_sparse, " from a list of ", sparse_values_list.size(),
            " values.");
      if (next_sparse >= sparse_indices_list.size())
        return errors::InvalidArgument(
            "input_order \"", input_order_,
            "\" specifies reading a sparse tensor index at index ",
            next_sparse, " from a list of ", sparse_indices_list.size(),
            " indices.");
      TF_RETURN_IF_ERROR(BuildSparseFeatureReader(
          sparse_indices_list[next_sparse], sparse_values_list[next_sparse],
          batch_size, features));
      next_sparse++;
    } else if (c == 'D') {
      if (next_dense >= dense_list.size())
        return errors::InvalidArgument(
            "input_order \"", input_order_,
            "\" specifies reading a dense tensor at index ", next_dense,
            " from a list of ", dense_list.size(), " tensors.");
      TF_RETURN_IF_ERROR(
          BuildDenseFeatureReader(dense_list[next_dense++], features));
    } else {
      return errors::InvalidArgument("Unexpected input_order value.");
    }
  }
  return Status::OK();
}

int submit_bio_wait(struct bio *bio)
{
	struct submit_bio_ret ret;
	init_completion(&ret.event);
	bio->bi_private = &ret;
	bio->bi_end_io = submit_bio_wait_endio;
	bio->bi_opf |= REQ_SYNC;
	submit_bio(bio);
	wait_for_completion_io(&ret.event);
	return ret.error;
}

debug_print_range(const char *msg,
    int lineno,
    void *start, signed long len,
    void *end)
{
    char *st = (char *)start;
    char *en = (char *)end;
    signed long le = len;
    if (len) {
        if (en) {
            le = (long)(en-st);
        } else {
            en= start+len;
        }
    } else if (en) {
        le = (long)(en-st);
    }
    printf("RANGEdebug %s  st=0x%lx le=%ld en=0x%lx line %d\n",
        msg,(unsigned long)st,le,(unsigned long)en,lineno);
}

_pdfioDictSetValue(
    pdfio_dict_t   *dict,		
    const char     *key,		
    _pdfio_value_t *value)		
{
  _pdfio_pair_t	*pair;			
  PDFIO_DEBUG("_pdfioDictSetValue(dict=%p, key=\"%s\", value=%p)\n", dict, key, (void *)value);
  if (dict->num_pairs > 0)
  {
    _pdfio_pair_t	pkey;		
    pkey.key = key;
    if ((pair = (_pdfio_pair_t *)bsearch(&pkey, dict->pairs, dict->num_pairs, sizeof(_pdfio_pair_t), (int (*)(const void *, const void *))compare_pairs)) != NULL)
    {
      PDFIO_DEBUG("_pdfioDictSetValue: Replacing existing value.\n");
      if (pair->value.type == PDFIO_VALTYPE_BINARY)
        free(pair->value.value.binary.data);
      pair->value = *value;
      return (true);
    }
  }
  if (dict->num_pairs >= dict->alloc_pairs)
  {
    _pdfio_pair_t *temp = (_pdfio_pair_t *)realloc(dict->pairs, (dict->alloc_pairs + 8) * sizeof(_pdfio_pair_t));
    if (!temp)
    {
      PDFIO_DEBUG("_pdfioDictSetValue: Out of memory.\n");
      return (false);
    }
    dict->pairs       = temp;
    dict->alloc_pairs += 8;
  }
  pair = dict->pairs + dict->num_pairs;
  dict->num_pairs ++;
  pair->key   = key;
  pair->value = *value;
  if (dict->num_pairs > 1 && compare_pairs(pair - 1, pair) > 0)
    qsort(dict->pairs, dict->num_pairs, sizeof(_pdfio_pair_t), (int (*)(const void *, const void *))compare_pairs);
#ifdef DEBUG
  PDFIO_DEBUG("_pdfioDictSetValue(%p): %lu pairs\n", (void *)dict, (unsigned long)dict->num_pairs);
  PDFIO_DEBUG("_pdfioDictSetValue(%p): ", (void *)dict);
  PDFIO_DEBUG_DICT(dict);
  PDFIO_DEBUG("\n");
#endif 
  return (true);
}

pf_remove_state(struct pf_state *st)
{
	PF_ASSERT_LOCKED();
	if (st->timeout == PFTM_UNLINKED)
		return;
	st->timeout = PFTM_UNLINKED;
	pf_postprocess_addr(st);
	if (st->src.state == PF_TCPS_PROXY_DST) {
		pf_send_tcp(st->rule.ptr, st->key[PF_SK_WIRE]->af,
		    &st->key[PF_SK_WIRE]->addr[1],
		    &st->key[PF_SK_WIRE]->addr[0],
		    st->key[PF_SK_WIRE]->port[1],
		    st->key[PF_SK_WIRE]->port[0],
		    st->src.seqhi, st->src.seqlo + 1,
		    TH_RST|TH_ACK, 0, 0, 0, 1, st->tag,
		    st->key[PF_SK_WIRE]->rdomain);
	}
	if (st->key[PF_SK_STACK]->proto == IPPROTO_TCP)
		pf_set_protostate(st, PF_PEER_BOTH, TCPS_CLOSED);
	RBT_REMOVE(pf_state_tree_id, &tree_id, st);
#if NPFLOW > 0
	if (st->state_flags & PFSTATE_PFLOW)
		export_pflow(st);
#endif	
#if NPFSYNC > 0
	pfsync_delete_state(st);
#endif	
	pf_src_tree_remove_state(st);
	pf_detach_state(st);
}

