void ipv4_pktinfo_prepare(const struct sock *sk, struct sk_buff *skb)
{
	struct in_pktinfo *pktinfo = PKTINFO_SKB_CB(skb);
	bool prepare = (inet_sk(sk)->cmsg_flags & IP_CMSG_PKTINFO) ||
		       ipv6_sk_rxinfo(sk);
	if (prepare && skb_rtable(skb)) {
		if (pktinfo->ipi_ifindex == LOOPBACK_IFINDEX)
			pktinfo->ipi_ifindex = inet_iif(skb);
		pktinfo->ipi_spec_dst.s_addr = fib_compute_spec_dst(skb);
	} else {
		pktinfo->ipi_ifindex = 0;
		pktinfo->ipi_spec_dst.s_addr = 0;
	}
	skb_dst_drop(skb);
}

int hugetlb_reserve_pages(struct inode *inode,
					long from, long to,
					struct vm_area_struct *vma,
					vm_flags_t vm_flags)
{
	long ret, chg;
	struct hstate *h = hstate_inode(inode);
	struct hugepage_subpool *spool = subpool_inode(inode);
	if (vm_flags & VM_NORESERVE)
		return 0;
	if (!vma || vma->vm_flags & VM_MAYSHARE)
		chg = region_chg(&inode->i_mapping->private_list, from, to);
	else {
		struct resv_map *resv_map = resv_map_alloc();
		if (!resv_map)
			return -ENOMEM;
		chg = to - from;
		set_vma_resv_map(vma, resv_map);
		set_vma_resv_flags(vma, HPAGE_RESV_OWNER);
	}
	if (chg < 0) {
		ret = chg;
		goto out_err;
	}
	if (hugepage_subpool_get_pages(spool, chg)) {
		ret = -ENOSPC;
		goto out_err;
	}
	ret = hugetlb_acct_memory(h, chg);
	if (ret < 0) {
		hugepage_subpool_put_pages(spool, chg);
		goto out_err;
	}
	if (!vma || vma->vm_flags & VM_MAYSHARE)
		region_add(&inode->i_mapping->private_list, from, to);
	return 0;
out_err:
	resv_map_put(vma);
	return ret;
}

void Compute(OpKernelContext* context) override {
  const Tensor& data = context->input(0);
  const Tensor& weights = context->input(1);
  bool use_weights = weights.NumElements() > 0;
  OP_REQUIRES(context,
              TensorShapeUtils::IsVector(data.shape()) ||
                  TensorShapeUtils::IsMatrix(data.shape()),
              errors::InvalidArgument(
                  "Input must be a 1 or 2-dimensional tensor. Got: ",
                  data.shape().DebugString()));
  if (use_weights) {
    OP_REQUIRES(
        context, weights.shape() == data.shape(),
        errors::InvalidArgument(
            "Weights and data must have the same shape. Weight shape: ",
            weights.shape().DebugString(),
            "; data shape: ", data.shape().DebugString()));
  }
  bool is_1d = TensorShapeUtils::IsVector(data.shape());
  int negative_valued_axis = -1;
  int num_batch_dimensions = (data.shape().dims() + negative_valued_axis);
  int num_batch_elements = 1;
  for (int i = 0; i < num_batch_dimensions; ++i) {
    OP_REQUIRES(context, data.shape().dim_size(i) != 0,
                errors::InvalidArgument(
                    "Invalid input: Shapes dimension cannot be 0."));
    num_batch_elements *= data.shape().dim_size(i);
  }
  int num_value_elements = data.shape().num_elements() / num_batch_elements;
  auto per_batch_counts = BatchedMap<W>(num_batch_elements);
  T max_value = 0;
  const auto data_values = data.flat<T>();
  const auto weight_values = weights.flat<W>();
  int i = 0;
  for (int b = 0; b < num_batch_elements; ++b) {
    for (int v = 0; v < num_value_elements; ++v) {
      const auto& value = data_values(i);
      if (value >= 0 && (maxlength_ <= 0 || value < maxlength_)) {
        if (binary_output_) {
          per_batch_counts[b][value] = 1;
        } else if (use_weights) {
          per_batch_counts[b][value] += weight_values(i);
        } else {
          per_batch_counts[b][value]++;
        }
        if (value > max_value) {
          max_value = value;
        }
      }
      ++i;
    }
  }
  int num_output_values = GetOutputSize(max_value, maxlength_, minlength_);
  OP_REQUIRES_OK(context, OutputSparse<W>(per_batch_counts, num_output_values,
                                          is_1d, context));
}

static size_t exif_convert_any_to_int(void *value, int format, int motorola_intel TSRMLS_DC)
{
	int 		s_den;
	unsigned 	u_den;
	switch(format) {
		case TAG_FMT_SBYTE:     return *(signed char *)value;
		case TAG_FMT_BYTE:      return *(uchar *)value;
		case TAG_FMT_USHORT:    return php_ifd_get16u(value, motorola_intel);
		case TAG_FMT_ULONG:     return php_ifd_get32u(value, motorola_intel);
		case TAG_FMT_URATIONAL:
			u_den = php_ifd_get32u(4+(char *)value, motorola_intel);
			if (u_den == 0) {
				return 0;
			} else {
				return php_ifd_get32u(value, motorola_intel) / u_den;
			}
		case TAG_FMT_SRATIONAL:
			s_den = php_ifd_get32s(4+(char *)value, motorola_intel);
			if (s_den == 0) {
				return 0;
			} else {
				return php_ifd_get32s(value, motorola_intel) / s_den;
			}
		case TAG_FMT_SSHORT:    return php_ifd_get16u(value, motorola_intel);
		case TAG_FMT_SLONG:     return php_ifd_get32s(value, motorola_intel);
		case TAG_FMT_SINGLE:
#ifdef EXIF_DEBUG
			php_error_docref(NULL TSRMLS_CC, E_NOTICE, "Found value of type single");
#endif
			return (size_t)*(float *)value;
		case TAG_FMT_DOUBLE:
#ifdef EXIF_DEBUG
			php_error_docref(NULL TSRMLS_CC, E_NOTICE, "Found value of type double");
#endif
			return (size_t)*(double *)value;
	}
	return 0;
}

int wait_for_key_construction(struct key *key, bool intr)
{
	int ret;
	ret = wait_on_bit(&key->flags, KEY_FLAG_USER_CONSTRUCT,
			  intr ? TASK_INTERRUPTIBLE : TASK_UNINTERRUPTIBLE);
	if (ret)
		return -ERESTARTSYS;
	if (test_bit(KEY_FLAG_NEGATIVE, &key->flags)) {
		smp_rmb();
		return key->reject_error;
	}
	return key_validate(key);
}

static void youngcollection (lua_State *L, global_State *g) {
  GCObject **psurvival;  
  lua_assert(g->gcstate == GCSpropagate);
  markold(g, g->survival, g->reallyold);
  markold(g, g->finobj, g->finobjrold);
  atomic(L);
  psurvival = sweepgen(L, g, &g->allgc, g->survival);
  sweepgen(L, g, psurvival, g->reallyold);
  g->reallyold = g->old;
  g->old = *psurvival;  
  g->survival = g->allgc;  
  psurvival = sweepgen(L, g, &g->finobj, g->finobjsur);
  sweepgen(L, g, psurvival, g->finobjrold);
  g->finobjrold = g->finobjold;
  g->finobjold = *psurvival;  
  g->finobjsur = g->finobj;  
  sweepgen(L, g, &g->tobefnz, NULL);
  finishgencycle(L, g);
}

