devzvol_create(struct vnode *dvp, char *nm, struct vattr *vap, vcexcl_t excl,
    int mode, struct vnode **vpp, struct cred *cred, int flag,
    caller_context_t *ct, vsecattr_t *vsecp)
{
	int error;
	struct vnode *vp;
	*vpp = NULL;
	error = devzvol_lookup(dvp, nm, &vp, NULL, 0, NULL, cred, ct, NULL,
	    NULL);
	if (error == 0) {
		if (excl == EXCL)
			error = EEXIST;
		else if (vp->v_type == VDIR && (mode & VWRITE))
			error = EISDIR;
		else
			error = VOP_ACCESS(vp, mode, 0, cred, ct);
		if (error) {
			VN_RELE(vp);
		} else
			*vpp = vp;
	} else if (error == ENOENT) {
		error = EROFS;
	}
	return (error);
}

bool Messageheader::Parser::state_fieldbody_crlf(char ch)
{
  if (ch == '\r')
    SET_STATE(state_end_cr);
  else if (ch == '\n')
  {
    log_debug("header " << fieldnamePtr << ": " << fieldbodyPtr);
    switch (header.onField(fieldnamePtr, fieldbodyPtr))
    {
      case OK:
      case END:  return true;
                 break;
      case FAIL: failedFlag = true;
                 log_warn("invalid character " << chartoprint(ch) << " in fieldbody");
                 break;
    }
    *headerdataPtr = '\0';
    return true;
  }
  else if (std::isspace(ch))
  {
    checkHeaderspace(1);
    *(headerdataPtr - 1) = '\n';
    *headerdataPtr++ = ch;
    SET_STATE(state_fieldbody);
  }
  else if (ch >= 33 && ch <= 126)
  {
    switch (header.onField(fieldnamePtr, fieldbodyPtr))
    {
      case OK:   SET_STATE(state_fieldname);
                 break;
      case FAIL: failedFlag = true;
                 log_warn("invalid character " << chartoprint(ch) << " in fieldbody");
                 break;
      case END:  return true;
                 break;
    }
    fieldnamePtr = headerdataPtr;
    checkHeaderspace(1);
    *headerdataPtr++ = ch;
  }
  return false;
}

struct btrfs_device *btrfs_find_device_by_devspec(
		struct btrfs_fs_info *fs_info, u64 devid,
		const char *device_path)
{
	struct btrfs_device *device;
	if (devid) {
		device = btrfs_find_device(fs_info->fs_devices, devid, NULL,
					   NULL);
		if (!device)
			return ERR_PTR(-ENOENT);
		return device;
	}
	if (!device_path || !device_path[0])
		return ERR_PTR(-EINVAL);
	if (strcmp(device_path, "missing") == 0) {
		list_for_each_entry(device, &fs_info->fs_devices->devices,
				    dev_list) {
			if (test_bit(BTRFS_DEV_STATE_IN_FS_METADATA,
				     &device->dev_state) && !device->bdev)
				return device;
		}
		return ERR_PTR(-ENOENT);
	}
	return btrfs_find_device_by_path(fs_info, device_path);
}

