StatusOr<std::vector<int64_t>> GetKernelSize(XlaOpKernelContext* ctx) {
  if (ctx->num_inputs() == 1) {
    return ksize_;
  }
  const TensorShape ksize_shape = ctx->InputShape(1);
  if (!TensorShapeUtils::IsVector(ksize_shape)) {
    return errors::InvalidArgument("ksize must be a vector, not shape ",
                                   ksize_shape.DebugString());
  }
  if (ksize_shape.num_elements() != num_dims()) {
    return errors::InvalidArgument(
        "Sliding window ksize field must "
        "specify ",
        num_dims(), " dimensions");
  }
  std::vector<int64_t> ksize;
  auto status = ctx->ConstantInputAsIntVector(1, &ksize);
  if (!status.ok()) {
    return status;
  }
  return ksize;
}

static int send_acknowledge(struct nci_spi *nspi, u8 acknowledge)
{
	struct sk_buff *skb;
	unsigned char *hdr;
	u16 crc;
	int ret;
	skb = nci_skb_alloc(nspi->ndev, 0, GFP_KERNEL);
	if (!skb)
		return -ENOMEM;
	hdr = skb_push(skb, NCI_SPI_HDR_LEN);
	hdr[0] = NCI_SPI_DIRECT_WRITE;
	hdr[1] = NCI_SPI_CRC_ENABLED;
	hdr[2] = acknowledge << NCI_SPI_ACK_SHIFT;
	hdr[3] = 0;
	crc = crc_ccitt(CRC_INIT, skb->data, skb->len);
	skb_put_u8(skb, crc >> 8);
	skb_put_u8(skb, crc & 0xFF);
	ret = __nci_spi_send(nspi, skb, 0);
	kfree_skb(skb);
	return ret;
}

static int xfrm_dump_policy(struct sk_buff *skb, struct netlink_callback *cb)
{
	struct net *net = sock_net(skb->sk);
	struct xfrm_policy_walk *walk = (struct xfrm_policy_walk *) &cb->args[1];
	struct xfrm_dump_info info;
	BUILD_BUG_ON(sizeof(struct xfrm_policy_walk) >
		     sizeof(cb->args) - sizeof(cb->args[0]));
	info.in_skb = cb->skb;
	info.out_skb = skb;
	info.nlmsg_seq = cb->nlh->nlmsg_seq;
	info.nlmsg_flags = NLM_F_MULTI;
	if (!cb->args[0]) {
		cb->args[0] = 1;
		xfrm_policy_walk_init(walk, XFRM_POLICY_TYPE_ANY);
	}
	(void) xfrm_policy_walk(net, walk, dump_one_policy, &info);
	return skb->len;
}

