		g_warning ("Failed to set archive type");
		goto out;
	}
	if (!ev_archive_open_filename (ar, argv[2], &error)) {
		g_warning ("Failed to open '%s': %s",
			   argv[2], error->message);
		g_error_free (error);
		goto out;
	}
	while (1) {
		const char *name;
		gboolean is_encrypted;
		gint64 size;
		if (!ev_archive_read_next_header (ar, &error)) {
			if (error != NULL) {
				g_warning ("Fatal error handling archive: %s", error->message);
				g_clear_error (&error);
				goto out;
			}
			break;
		}
		name = ev_archive_get_entry_pathname (ar);
		is_encrypted = ev_archive_get_entry_is_encrypted (ar);
		size = ev_archive_get_entry_size (ar);
		if (!printed_header) {
			g_print ("P\tSIZE\tNAME\n");
			printed_header = TRUE;
		}
		g_print ("%c\t%"G_GINT64_FORMAT"\t%s\n",
			 is_encrypted ? 'P' : ' ',
			 size, name);
	}
	ev_archive_reset (ar);
	g_clear_object (&ar);
	return 0;
out:
	g_clear_object (&ar);
	return 1;
}

Status CheckInputs(Tensor group_size_t, Tensor group_key_t) {
  if (group_size_t.dims() > 0) {
    return errors::Internal(
        "Unexpected dimensions on input group_size. "
        "It shoulbe a scalar, got tensor with shape ",
        group_size_t.shape().DebugString());
  }
  if (group_key_t.dims() > 0) {
    return errors::Internal("Unexpected dimensions on input group_key, got ",
                            group_key_t.shape().DebugString());
  }
  auto group_size = group_size_t.unaligned_flat<int32>()(0);
  if (group_size <= 0) {
    return errors::InvalidArgument(
        "group_size must be positive integer but got ", group_size);
  }
  return Status::OK();
}

u32 gf_isom_get_nalu_length_field(GF_ISOFile *file, u32 track, u32 StreamDescriptionIndex)
{
	GF_TrackBox *trak;
	GF_SampleEntryBox *entry;
	GF_MPEGVisualSampleEntryBox *ve;
	GF_SampleDescriptionBox *stsd;
	trak = gf_isom_get_track_from_file(file, track);
	if (!trak) {
		file->LastError = GF_BAD_PARAM;
		return 0;
	}
	stsd = trak->Media->information->sampleTable->SampleDescription;
	if (!stsd || !StreamDescriptionIndex || StreamDescriptionIndex > gf_list_count(stsd->child_boxes)) {
		file->LastError = GF_BAD_PARAM;
		return 0;
	}
	entry = (GF_SampleEntryBox *)gf_list_get(stsd->child_boxes, StreamDescriptionIndex - 1);
	if (!entry || ! gf_isom_is_nalu_based_entry(trak->Media, entry)) {
		file->LastError = GF_BAD_PARAM;
		return 0;
	}
	ve = (GF_MPEGVisualSampleEntryBox*)entry;
	if (ve->avc_config) return ve->avc_config->config->nal_unit_size;
	if (ve->svc_config) return ve->svc_config->config->nal_unit_size;
	if (ve->hevc_config) return ve->hevc_config->config->nal_unit_size;
	if (ve->lhvc_config) return ve->lhvc_config->config->nal_unit_size;
	return 0;
}

