static int proc_open(const char *path, struct fuse_file_info *fi)
{
	int type = -1;
	struct file_info *info;
	if (strcmp(path, "/proc/meminfo") == 0)
		type = LXC_TYPE_PROC_MEMINFO;
	else if (strcmp(path, "/proc/cpuinfo") == 0)
		type = LXC_TYPE_PROC_CPUINFO;
	else if (strcmp(path, "/proc/uptime") == 0)
		type = LXC_TYPE_PROC_UPTIME;
	else if (strcmp(path, "/proc/stat") == 0)
		type = LXC_TYPE_PROC_STAT;
	else if (strcmp(path, "/proc/diskstats") == 0)
		type = LXC_TYPE_PROC_DISKSTATS;
	if (type == -1)
		return -ENOENT;
	info = malloc(sizeof(*info));
	if (!info)
		return -ENOMEM;
	memset(info, 0, sizeof(*info));
	info->type = type;
	info->buflen = get_procfile_size(path) + BUF_RESERVE_SIZE;
	do {
		info->buf = malloc(info->buflen);
	} while (!info->buf);
	memset(info->buf, 0, info->buflen);
	info->size = info->buflen;
	fi->fh = (unsigned long)info;
	return 0;
}

explicit QuantizeAndDequantizeV2Op(OpKernelConstruction* ctx)
    : OpKernel(ctx) {
  OP_REQUIRES_OK(ctx, ctx->GetAttr("signed_input", &signed_input_));
  OP_REQUIRES_OK(ctx, ctx->GetAttr("axis", &axis_));
  OP_REQUIRES_OK(ctx, ctx->GetAttr("num_bits", &num_bits_));
  OP_REQUIRES(ctx, num_bits_ > 0 && num_bits_ < (signed_input_ ? 62 : 63),
              errors::InvalidArgument("num_bits is out of range: ", num_bits_,
                                      " with signed_input_ ", signed_input_));
  OP_REQUIRES_OK(ctx, ctx->GetAttr("range_given", &range_given_));
  string round_mode_string;
  OP_REQUIRES_OK(ctx, ctx->GetAttr("round_mode", &round_mode_string));
  OP_REQUIRES(
      ctx,
      (round_mode_string == "HALF_UP" || round_mode_string == "HALF_TO_EVEN"),
      errors::InvalidArgument("Round mode string must be "
                              "'HALF_UP' or "
                              "'HALF_TO_EVEN', is '" +
                              round_mode_string + "'"));
  if (round_mode_string == "HALF_UP") {
    round_mode_ = ROUND_HALF_UP;
  } else if (round_mode_string == "HALF_TO_EVEN") {
    round_mode_ = ROUND_HALF_TO_EVEN;
  }
  OP_REQUIRES_OK(ctx, ctx->GetAttr("narrow_range", &narrow_range_));
}

int gru_set_context_option(unsigned long arg)
{
	struct gru_thread_state *gts;
	struct gru_set_context_option_req req;
	int ret = 0;
	STAT(set_context_option);
	if (copy_from_user(&req, (void __user *)arg, sizeof(req)))
		return -EFAULT;
	gru_dbg(grudev, "op %d, gseg 0x%lx, value1 0x%lx\n", req.op, req.gseg, req.val1);
	gts = gru_find_lock_gts(req.gseg);
	if (!gts) {
		gts = gru_alloc_locked_gts(req.gseg);
		if (IS_ERR(gts))
			return PTR_ERR(gts);
	}
	switch (req.op) {
	case sco_blade_chiplet:
		if (req.val0 < -1 || req.val0 >= GRU_CHIPLETS_PER_HUB ||
		    req.val1 < -1 || req.val1 >= GRU_MAX_BLADES ||
		    (req.val1 >= 0 && !gru_base[req.val1])) {
			ret = -EINVAL;
		} else {
			gts->ts_user_blade_id = req.val1;
			gts->ts_user_chiplet_id = req.val0;
			gru_check_context_placement(gts);
		}
		break;
	case sco_gseg_owner:
		gts->ts_tgid_owner = current->tgid;
		break;
	case sco_cch_req_slice:
		gts->ts_cch_req_slice = req.val1 & 3;
		break;
	default:
		ret = -EINVAL;
	}
	gru_unlock_gts(gts);
	return ret;
}

static void iwl_sta_ucode_activate(struct iwl_priv *priv, u8 sta_id)
{
	if (!(priv->stations[sta_id].used & IWL_STA_DRIVER_ACTIVE))
		IWL_ERR(priv, "ACTIVATE a non DRIVER active station id %u "
			"addr %pM\n",
			sta_id, priv->stations[sta_id].sta.sta.addr);
	if (priv->stations[sta_id].used & IWL_STA_UCODE_ACTIVE) {
		IWL_DEBUG_ASSOC(priv,
				"STA id %u addr %pM already present in uCode "
				"(according to driver)\n",
				sta_id, priv->stations[sta_id].sta.sta.addr);
