static int sysfs_slab_add(struct kmem_cache *s)
{
	int err;
	const char *name;
	struct kset *kset = cache_kset(s);
	int unmergeable = slab_unmergeable(s);
	INIT_WORK(&s->kobj_remove_work, sysfs_slab_remove_workfn);
	if (!kset) {
		kobject_init(&s->kobj, &slab_ktype);
		return 0;
	}
	if (!unmergeable && disable_higher_order_debug &&
			(slub_debug & DEBUG_METADATA_FLAGS))
		unmergeable = 1;
	if (unmergeable) {
		sysfs_remove_link(&slab_kset->kobj, s->name);
		name = s->name;
	} else {
		name = create_unique_id(s);
	}
	s->kobj.kset = kset;
	err = kobject_init_and_add(&s->kobj, &slab_ktype, NULL, "%s", name);
	if (err)
		goto out;
	err = sysfs_create_group(&s->kobj, &slab_attr_group);
	if (err)
		goto out_del_kobj;
#ifdef CONFIG_MEMCG
	if (is_root_cache(s) && memcg_sysfs_enabled) {
		s->memcg_kset = kset_create_and_add("cgroup", NULL, &s->kobj);
		if (!s->memcg_kset) {
			err = -ENOMEM;
			goto out_del_kobj;
		}
	}
#endif
	kobject_uevent(&s->kobj, KOBJ_ADD);
	if (!unmergeable) {
		sysfs_slab_alias(s, s->name);
	}
out:
	if (!unmergeable)
		kfree(name);
	return err;
out_del_kobj:
	kobject_del(&s->kobj);
	goto out;

static int open_if_safe(int dirfd, const char *nextpath)
{
	int newfd = openat(dirfd, nextpath, O_RDONLY | O_NOFOLLOW);
	if (newfd >= 0) 
		return newfd;
	if (errno == ELOOP)
		return newfd;
	if (errno == EPERM || errno == EACCES) {
		newfd = openat(dirfd, nextpath, O_PATH | O_NOFOLLOW);
		if (newfd >= 0) {
			int ret = check_symlink(newfd);
			if (ret < 0) {
				close(newfd);
				newfd = ret;
			}
		}
	}
	return newfd;
}

int use_conf(char *test_path)
{
    int ret;
    size_t flags = 0;
    char filename[1024], errstr[1024];
    char *buffer;
    FILE *infile, *conffile;
    json_t *json;
    json_error_t error;
    sprintf(filename, "%s%cinput", test_path, dir_sep);
    if (!(infile = fopen(filename, "rb"))) {
        fprintf(stderr, "Could not open \"%s\"\n", filename);
        return 2;
    }
    sprintf(filename, "%s%cenv", test_path, dir_sep);
    conffile = fopen(filename, "rb");
    if (conffile) {
        read_conf(conffile);
        fclose(conffile);
    }
    if (conf.indent < 0 || conf.indent > 255) {
        fprintf(stderr, "invalid value for JSON_INDENT: %d\n", conf.indent);
        return 2;
    }
    if (conf.indent)
        flags |= JSON_INDENT(conf.indent);
    if (conf.compact)
        flags |= JSON_COMPACT;
    if (conf.ensure_ascii)
        flags |= JSON_ENSURE_ASCII;
    if (conf.preserve_order)
        flags |= JSON_PRESERVE_ORDER;
    if (conf.sort_keys)
        flags |= JSON_SORT_KEYS;
    if (conf.have_hashseed)
        json_object_seed(conf.hashseed);
    if (conf.strip) {
        buffer = loadfile(infile);
        json = json_loads(strip(buffer), 0, &error);
        free(buffer);
    }
    else
        json = json_loadf(infile, 0, &error);
    fclose(infile);
    if (!json) {
        sprintf(errstr, "%d %d %d\n%s\n",
                error.line, error.column, error.position,
                error.text);
        ret = cmpfile(errstr, test_path, "error");
        return ret;
    }
    buffer = json_dumps(json, flags);
    ret = cmpfile(buffer, test_path, "output");
    free(buffer);
    json_decref(json);
    return ret;
}

